"use strict";(self.webpackChunkklipper_docs=self.webpackChunkklipper_docs||[]).push([["99584"],{41681:function(n,e,t){t.r(e),t.d(e,{frontMatter:()=>s,default:()=>c,contentTitle:()=>a,assets:()=>p,toc:()=>_,metadata:()=>i});var i=JSON.parse('{"id":"ProductDoc/MainBoard/fly-d/fly-dp5/cfg","title":"Klipper Reference Configuration","description":"","source":"@site/i18n/en/docusaurus-plugin-content-docs/current/ProductDoc/MainBoard/fly-d/fly-dp5/cfg.mdx","sourceDirName":"ProductDoc/MainBoard/fly-d/fly-dp5","slug":"/ProductDoc/MainBoard/fly-d/fly-dp5/cfg","permalink":"/fly-docs-next/en/docs/ProductDoc/MainBoard/fly-d/fly-dp5/cfg","draft":false,"unlisted":false,"editUrl":"https://github.com/kluoyun/fly-docs-next/tree/master/docs/ProductDoc/MainBoard/fly-d/fly-dp5/cfg.mdx","tags":[],"version":"current","lastUpdatedBy":"xishangyue","lastUpdatedAt":1746004502000,"sidebarPosition":5,"frontMatter":{"sidebar_position":5,"sidebar_label":"Klipper Reference Configuration"},"sidebar":"tutorialSidebar","previous":{"title":"Mainboard Wiring","permalink":"/fly-docs-next/en/docs/ProductDoc/MainBoard/fly-d/fly-dp5/wiring"},"next":{"title":"CAN Bridge","permalink":"/fly-docs-next/en/docs/ProductDoc/MainBoard/fly-d/fly-dp5/canbridge"}}'),o=t(85893),r=t(50065);let s={sidebar_position:5,sidebar_label:"Klipper Reference Configuration"},a="Klipper Reference Configuration",p={},_=[];function d(n){let e={code:"code",h1:"h1",header:"header",pre:"pre",...(0,r.a)(),...n.components};return(0,o.jsxs)(o.Fragment,{children:[(0,o.jsx)(e.header,{children:(0,o.jsx)(e.h1,{id:"klipper-reference-configuration",children:"Klipper Reference Configuration"})}),"\n",(0,o.jsx)(e.pre,{children:(0,o.jsx)(e.code,{className:"language-cfg",children:'####################################################################################\n#                         3D MELLOW / (Add as needed)                             #\n####################################################################################\n## Fly-DP5 documentation website:\n## Fly-DP5 schematic website:\n## FLY Official Taobao Store: https://shop126791347.taobao.com/shop/view_shop.htm?spm=a230r.1.14.4.1a4840a8hyvpPJ&user_number_id=2464680006\n## For after-sales service, please contact Taobao customer service\n## FLY After-sales Technical Support Group: 621032883\n## FLY-RRF Firmware Discussion Group: 786561979\n\n#####################################################################\n#                               Notes                               #\n#####################################################################\n##***Items to change/check:***\n## MCU path                                [mcu]\n## Printer movement range                   xyz position\n## Thermistor type                          [extruder] and [heater_bed]\n## Z endstop position                       [safe_z_home]\n## Z endstop offset                         [stepper_z]\n## PID tuning                               [extruder] and [heater_bed]\n## Extruder motor step tuning               [extruder]\n\n#####################################################################\n#                               File Includes                       #\n#####################################################################\n#[include fluidd.cfg]          # FLUIDD include file.\n#[include mainsail.cfg]        # MAINSDIL include file.\n#Determine which file to use as needed\n\n#####################################################################\n#                               Mainboard Configuration             #\n#####################################################################\n[mcu]                           # FLY board ID\nserial: /dev/serial/by-id/usb-Klipper_stm32f072xb_************************\n### To check USB firmware id: ls /dev/serial/by-id/\n### Replace /dev/serial/by-id/usb-Klipper_stm32f072xb_************************ with your id\n#canbus_uuid: e51d5c71a901\n### To check CAN firmware id: ~/klippy-env/bin/python ~/klipper/scripts/canbus_query.py can0\n### For CAN, replace serial with canbus_uuid: and add the id\n\n#####################################################################\n#                            Printer and Acceleration               #\n#####################################################################\n[printer]                       # Printer settings\nkinematics: corexy              # Kinematics: cartesian or corexy, etc.\nmax_velocity: 300               # Max printer speed\nmax_accel: 3000                 # Max acceleration (max 3000)\nmax_z_velocity: 15              # Max Z speed\nmax_z_accel: 100                # Max Z acceleration\nsquare_corner_velocity: 5.0     # Square corner velocity, lower to reduce inertia from heavy beds\n\n#####################################################################\n#                             Temperature Monitoring                #\n#####################################################################\n[temperature_sensor Fly-DP5]       # FLY board temperature\nsensor_type: temperature_mcu       # Linked to mcu (default)\n#####################################################################\n#[temperature_sensor FLY-\u03C0]        # Host temperature\n#sensor_type: temperature_host      # Linked to host\n\n#####################################################################\n#                              Bed Mesh Calibration                 #\n#####################################################################\n[bed_mesh]\nspeed: 50                    # Calibration speed\nhorizontal_move_z: 5         # Z lift before moving to next point\nmesh_min: 30,30              # Min calibration point (x, y)\nmesh_max: 270, 270           # Max calibration point (x, y)\nprobe_count: 4,4             # Probe points (4x4 = 16 points)\nmesh_pps: 2,2                # Extra probe points\nalgorithm: bicubic           # Algorithm model\nbicubic_tension: 0.2         # Do not change\n\n#####################################################################\n#             X/Y Stepper Motor Settings                            #\n#####################################################################\n#                       B__________A\n#                            Z0\n#\n#                          12864\n\n#####################################################################\n#                  X Stepper Motor      (Motor B)                   #\n#####################################################################\n## DRIVER0 motor position\n[stepper_x]\nstep_pin: PC15\ndir_pin: PC14\nenable_pin: !PC2\nrotation_distance: 40               # Pulley circumference mm (2GT-20T: 40, 2GT-16T: 32)\nmicrosteps: 16\nfull_steps_per_rotation: 200        # Steps per revolution (1.8\xb0: 200, 0.9\xb0: 400)\nendstop_pin: ^PB4                   # Endstop pin, normally closed recommended\n#endstop_pin: tmc5160_stepper_x:virtual_endstop        # Sensorless homing\n### Add ! to invert if needed, prevents crashes if wire breaks\nposition_min: 0\nposition_endstop: 300\nposition_max: 300\nhoming_speed: 200\nhoming_retract_dist: 5\n#####################################################################\n##  Ensure correct driver type (2208 or 2209)\n[tmc2209 stepper_x]\nuart_pin: PC13\ninterpolate: False\nrun_current: 0.8\nsense_resistor: 0.110\nstealthchop_threshold: 0\n#####################################################################\n# [tmc5160 stepper_x]\n# cs_pin: PC13\n# spi_bus: spi2\n# run_current: 1.0\n# interpolate: False\n# sense_resistor: 0.075   # Use 0.033 for 5160 Pro\n# stealthchop_threshold: 0\n\n#####################################################################\n#                  Y Stepper Motor      (Motor A)                   #\n#####################################################################\n## DRIVER1 motor position\n[stepper_y]\nstep_pin: PA1\ndir_pin: PA0\nenable_pin: !PA2\nrotation_distance: 40\nmicrosteps: 16\nfull_steps_per_rotation: 200\nendstop_pin: ^PB3\n#endstop_pin: tmc5160_stepper_x:virtual_endstop\n### Add ! to invert if needed, prevents crashes if wire breaks\nposition_min: 0\nposition_endstop: 300\nposition_max: 300\nhoming_speed: 200\nhoming_retract_dist: 5\n#####################################################################\n##  Ensure correct driver type (2209 or 5160)\n[tmc2209 stepper_y]\nuart_pin: PC3\ninterpolate: False\nrun_current: 0.8\nsense_resistor: 0.110\nstealthchop_threshold: 0\n#####################################################################\n#[tmc5160 stepper_y]\n#cs_pin: PC3\n#spi_bus: spi2\n#run_current: 1.0\n#interpolate: False\n#sense_resistor: 0.075   # Use 0.033 for 5160 Pro\n#stealthchop_threshold: 0\n\n#####################################################################\n#                     Z Stepper Motor                               #\n#####################################################################\n## DRIVER2 motor position\n[stepper_z]\nstep_pin: PA5\ndir_pin: !PA4\nenable_pin: !PA6\nrotation_distance: 8         # Leadscrew pitch, adjust as needed\nmicrosteps: 16\nfull_steps_per_rotation: 200\nendstop_pin: ^PD2\nposition_max: 290\nposition_endstop: -0.5\nposition_min: -5\nhoming_speed: 10\nsecond_homing_speed: 3\nhoming_retract_dist: 3\n#####################################################################\n[tmc2209 stepper_z]\nuart_pin: PA3\ninterpolate: false\nrun_current: 0.8\nsense_resistor: 0.110\nstealthchop_threshold: 0\n######################################################################\n## DRIVER4 motor position\n#[stepper_z1]\n#step_pin: PB10\n#dir_pin:  PB2\n#enable_pin: !PB11\n#rotation_distance: 8\n#microsteps: 32\n\n#[tmc2209 stepper_z1]\n#uart_pin: PB1\n#interpolate: false\n#run_current: 1.0\n#sense_resistor: 0.110\n#stealthchop_threshold: 0\n\n#####################################################################\n#                             Extruder Configuration                #\n#####################################################################\n## DRIVER7 motor position\n[extruder]\nstep_pin:PC5\ndir_pin:PC4\nenable_pin: !PB0\nmicrosteps: 16\nfull_steps_per_rotation: 200\nrotation_distance: 22.52245\n# Step calibration: 23.1325301 = old value 22.6789511 * actual 102 / target 100\ngear_ratio: 50:10                   # Gear ratio (Galileo: 7.5:1, comment out; BMG: 50:17, output shaft front, input shaft rear)\nnozzle_diameter: 0.400\nfilament_diameter: 1.750\nheater_pin: PC6\nsensor_type: Generic 3950\nsensor_pin: PC1\nmax_power: 1.0\nmin_temp: -235\nmax_temp: 350\nmin_extrude_temp: 150\npressure_advance: 0.04\npressure_advance_smooth_time:0.040\n# Nozzle PID tune: "PID_CALIBRATE HEATER=extruder TARGET=245"\ncontrol = pid\npid_kp = 26.213\npid_ki = 1.304\npid_kd = 131.721\n\n[tmc2209 extruder]\nuart_pin:PA7\ninterpolate: False\nrun_current: 0.6\nsense_resistor: 0.110\nstealthchop_threshold: 0\n\n#####################################################################\n#                            Heated Bed Configuration               #\n#####################################################################\n[heater_bed]\nheater_pin: PC7\nsensor_type: Generic 3950\nsensor_pin: PC0\nmax_power: 1.0\nmin_temp: -235\nmax_temp: 120\n# Bed PID tune: "PID_CALIBRATE HEATER=heater_bed TARGET=100"\ncontrol: pid\npid_kp: 58.437\npid_ki: 2.347\npid_kd: 363.769\n#####################################################################\n#                             Fan Configuration                     #\n#####################################################################\n[fan]\npin:PC9\nkick_start_time: 1.0\noff_below: 0.10\nmax_power: 1.0\n#####################################################################\n[heater_fan Heatbreak]\npin:PC8\nmax_power: 1.0\nkick_start_time: 0.5\nheater: extruder\nheater_temp: 50\nfan_speed: 1.0\n\n#####################################################################\n#                           Idle Bed Shutdown                       #\n#####################################################################\n[idle_timeout]\ntimeout: 1800                # Turn off bed after 30 minutes idle\n\n#####################################################################\n#                           Bed Leveling Probe                      #\n#####################################################################\n# Default PL08N probe should not be lower than nozzle, for leveling only.\n# If your PL08N is NO (normally open), add ! to the pin.\n[probe]\npin: ^PB5\nx_offset: 0\ny_offset: 25.0\nz_offset: 0\nspeed: 10.0\nsamples: 3\nsamples_result: median\nsample_retract_dist: 4.0\nsamples_tolerance: 0.007\nsamples_tolerance_retries: 3\n#####################################################################\n#[bltouch]\n#sensor_pin: ^PB5\n#control_pin: PA8\n#x_offset: 0\n#y_offset: 25.0\n#z_offset: 0\n\n#####################################################################\n#                               Homing                              #\n#####################################################################\n[safe_z_home]\nhome_xy_position:206,300\nspeed:100\nz_hop:10\n#####################################################################\n#[homing_override]\n#axes: z\n#set_position_z: 0\n#gcode:\n#   G90\n#   G0 Z15 F600\n#   G28 X Y\n#    ## Z endstop XY position\n#    ## After, update X0 and Y0 to your values (e.g. X157, Y305)\n#    ## Z endstop position\n#   G0 X185 Y250 F3600    #250mm\n#   G28 Z\n#   G0 Z10 F1800\n\n#####################################################################\n#                          12864 Display Configuration              #\n#####################################################################\n[board_pins]\naliases:\n    EXP1_1=NC,   EXP1_3=PC11,  EXP1_5=PC10, EXP1_7=NC, EXP1_9=<GND>,\n    EXP1_2=PA15, EXP1_4=PA14,  EXP1_6=PA13, EXP1_8=NC, EXP1_10=<5V>,\n    # EXP2 header\n    EXP2_1=PB14, EXP2_3=PC12, EXP2_5=PB6,   EXP2_7=NC,    EXP2_9=<GND>,\n    EXP2_2=PB13, EXP2_4=PB12, EXP2_6=PB15,  EXP2_8=<RST>, EXP2_10=<NC>,\n#####################################################################\n[display]\nlcd_type: uc1701\ncs_pin: EXP1_3\na0_pin: EXP1_4\nrst_pin: EXP1_5\ncontrast: 63\nencoder_pins: ^EXP2_5, ^!EXP2_3\nclick_pin: ^!EXP1_2\n####################################################################\n#### For FLY Mini12864\n[neopixel fly_mini12864]\npin: EXP1_6\nchain_count: 3\ninitial_RED: 0.5\ninitial_GREEN: 0.5\ninitial_BLUE: 0.5\ncolor_order: RGB\n####################################################################\n[neopixel TP_led]\npin:PB7\nchain_count: 50\ncolor_order: GRB\ninitial_RED: 0.0\ninitial_GREEN: 0.1\ninitial_BLUE: 0.0\n#####################################################################\n#                           Custom G-code Macros                    #\n#####################################################################\n[gcode_macro PRINT_START]\ngcode:\n    G92 E0\n    G28\n    G1 Z20 F3000\n    #SET_LED LED=LEDlight RED=0.2 GREEN=0.2 BLUE=0.5\n    #M117 Printing\n\n#####################################################################\n[gcode_macro PRINT_HUAXIAN]\ngcode:\n    G1 Z5 F3000\n    G92 E0\n    G90\n    G0 X5 Y1 F6000\n    G0 Z0.4\n    G91\n    G1 X100 E20 F1200\n    G1 Y1\n    G1 X-100 E20 F1200\n    G0 z5\n    G1 E-5.0 F3600\n    G92 E0\n    G90\n\n#####################################################################\n[gcode_macro PRINT_END]\ngcode:\n    #   Get Boundaries\n    {% set max_x = printer.configfile.config["stepper_x"]["position_max"]|float %}\n    {% set max_y = printer.configfile.config["stepper_y"]["position_max"]|float %}\n    {% set max_z = printer.configfile.config["stepper_z"]["position_max"]|float %}\n\n    #   Check end position to determine safe directions to move\n    {% if printer.toolhead.position.x < (max_x - 20) %}\n        {% set x_safe = 20.0 %}\n    {% else %}\n        {% set x_safe = -20.0 %}\n    {% endif %}\n\n    {% if printer.toolhead.position.y < (max_y - 20) %}\n        {% set y_safe = 20.0 %}\n    {% else %}\n        {% set y_safe = -20.0 %}\n    {% endif %}\n\n    {% if printer.toolhead.position.z < (max_z - 2) %}\n        {% set z_safe = 2.0 %}\n    {% else %}\n        {% set z_safe = max_z - printer.toolhead.position.z %}\n    {% endif %}\n\n    M400\n    G92 E0\n    G1 E-10.0 F3600\n    G91\n    G0 Z{z_safe} F3600\n    G0 X{x_safe} Y{y_safe} F20000\n    M104 S0\n    M140 S0\n    M106 S0\n    G90\n    G0 X{max_x / 2} Y{max_y} F3600\n    BED_MESH_CLEAR\n\n\n'})})]})}function c(n={}){let{wrapper:e}={...(0,r.a)(),...n.components};return e?(0,o.jsx)(e,{...n,children:(0,o.jsx)(d,{...n})}):d(n)}},50065:function(n,e,t){t.d(e,{Z:()=>a,a:()=>s});var i=t(67294);let o={},r=i.createContext(o);function s(n){let e=i.useContext(r);return i.useMemo(function(){return"function"==typeof n?n(e):{...e,...n}},[e,n])}function a(n){let e;return e=n.disableParentContext?"function"==typeof n.components?n.components(o):n.components||o:s(n.components),i.createElement(r.Provider,{value:e},n.children)}}}]);