"use strict";(self.webpackChunkklipper_docs=self.webpackChunkklipper_docs||[]).push([["48344"],{5996:function(e,n,t){t.r(n),t.d(n,{default:()=>d,frontMatter:()=>s,metadata:()=>r,assets:()=>c,toc:()=>l,contentTitle:()=>a});var r=JSON.parse('{"id":"ProductDoc/ToolBoard/fly-sb2040/sb2040-v3/cfg","title":"Reference Configuration","description":"","source":"@site/i18n/en/docusaurus-plugin-content-docs/current/ProductDoc/ToolBoard/fly-sb2040/sb2040-v3/cfg.mdx","sourceDirName":"ProductDoc/ToolBoard/fly-sb2040/sb2040-v3","slug":"/ProductDoc/ToolBoard/fly-sb2040/sb2040-v3/cfg","permalink":"/fly-docs-next/en/docs/ProductDoc/ToolBoard/fly-sb2040/sb2040-v3/cfg","draft":false,"unlisted":false,"editUrl":"https://github.com/kluoyun/fly-docs-next/tree/master/docs/ProductDoc/ToolBoard/fly-sb2040/sb2040-v3/cfg.mdx","tags":[],"version":"current","lastUpdatedBy":"xishangyue","lastUpdatedAt":1746004502000,"sidebarPosition":5,"frontMatter":{"sidebar_position":5,"sidebar_label":"Reference Configuration"},"sidebar":"tutorialSidebar","previous":{"title":"Motherboard Reference Wiring","permalink":"/fly-docs-next/en/docs/ProductDoc/ToolBoard/fly-sb2040/sb2040-v3/wiring"},"next":{"title":"Eddy Reference Configuration","permalink":"/fly-docs-next/en/docs/ProductDoc/ToolBoard/fly-sb2040/sb2040-v3/cfg2"}}'),o=t("85893"),i=t("50065");let s={sidebar_position:5,sidebar_label:"Reference Configuration"},a="Reference Configuration",c={},l=[];function p(e){let n={code:"code",h1:"h1",header:"header",pre:"pre",...(0,i.a)(),...e.components};return(0,o.jsxs)(o.Fragment,{children:[(0,o.jsx)(n.header,{children:(0,o.jsx)(n.h1,{id:"reference-configuration",children:"Reference Configuration"})}),"\n",(0,o.jsx)(n.pre,{children:(0,o.jsx)(n.code,{className:"language-cfg",children:"\n#####################################################################\n#      Notes\n#####################################################################\n## Please add [include SB2040.cfg] under printer.cfg\n## The tool board will use the following configurations,\n## and if the following configurations are used in other configurations, conflicts may occur.\n## [resonance_tester] && [extruder] && [tmc2209 extruder] && [fan] && [heater_fan my_hotend_fan]\n## [probe] && [bltouch] && [stepper_x]\n\n#####################################################################\n#      Master ID Configuration\n#####################################################################\n[mcu SB2040]  # Tool board serial number\n## CAN ID\n## ~/klippy-env/bin/python ~/klipper/scripts/canbus_query.py can0\ncanbus_uuid:\n#--------------------------------------------------------------------\n# # RS232 ID\n## Please fill in the baud rate according to the firmware configuration.\n# serial: /dev/serial/by-path/platform-1c1c000.usb-usb-0:1.2:1.0-port0\n# baud: 250000\n# restart_method:command\n\n#####################################################################\n#      Temperature monitoring\n#####################################################################\n[temperature_sensor SB2040]\nsensor_type: temperature_mcu\nsensor_mcu: SB2040\n\n#####################################################################\n#      Accelerometer\n#####################################################################\n## https://www.klipper3d.org/Measuring_Resonances.html?h=adxl#adxl345\n[lis2dw]\ncs_pin: SB2040:gpio12\nspi_bus:spi0_gpio4_gpio3_gpio2\n#--------------------------------------------------------------------\n[resonance_tester]\naccel_chip: lis2dw\nprobe_points: 150, 150, 20\n# Somewhere slightly above the middle of your print bed\nmin_freq: 5\nmax_freq: 133\naccel_per_hz: 75\nhz_per_sec: 1\n\n#####################################################################\n#      Stealthburner LED\n#####################################################################\n## https://github.com/VoronDesign/Voron-Stealthburner/tree/main\n[neopixel sb_leds]\npin: SB2040:gpio26\nchain_count: 16\n# Number of LEDs\ncolor_order: GRB\ninitial_RED: 0.4    #66CCFF\ninitial_GREEN: 0.8\ninitial_BLUE: 1\ninitial_WHITE: 0.0\n\n#####################################################################\n#      Extruder thermal sensitivity\n#####################################################################\n## Please select the type of thermocouple you want to use.\n[extruder]\n## Type of sensor - common thermistors are (Generic 3950, ATC Semitec 104GT-2)\nsensor_type:ATC Semitec 104GT-2\nsensor_pin:SB2040:gpio27\n###------------------------------------------------------------------\n## If using PT1000, please connect the jumper next to the thermal sensitivity.\n# sensor_type:PT1000\n# sensor_pin:SB2040:gpio27\n# pullup_resistor: 1000\n###------------------------------------------------------------------\n## Using MAX31865 to connect PT100 or PT1000\n# sensor_type: MAX31865\n# sensor_pin: SB2040:gpio17\n# spi_bus:spi0_gpio4_gpio3_gpio2\n# rtd_reference_r: 430  ## Set to 430 for PT100, 4300 for PT1000\n#rtd_nominal_r: 100  ## Set to 100 for PT100, 1000 for PT1000\n\n#####################################################################\n#      extruder\n#####################################################################\n## https://www.klipper3d.org/Config_Reference.html#extruder\n[extruder]\nstep_pin:SB2040:gpio14\ndir_pin:SB2040:gpio13\nenable_pin: !SB2040:gpio16\nrotation_distance: 21.84\n## rotation_distance = The original rotation_distance multiplied by the actual extrusion length divided by the requested extrusion length.\n## Calibration: 22.44 = old value 22 * actual value 102 / target value 100\ngear_ratio:50:10\n## Gear ratio (Galileo 7.5:1 and comment this line; BMG is 50:17, output shaft in front, input shaft in back)\nmicrosteps:16\nfull_steps_per_rotation: 200\nnozzle_diameter:0.400\nfilament_diameter:1.75\nheater_pin:SB2040:gpio23\nmin_temp:-200\nmax_temp:350\nmax_power:1.0\nmin_extrude_temp:10\npressure_advance: 0.05\npressure_advance_smooth_time: 0.040\n#max_extrude_only_distance: 200.0   # If extrusion flow error occurs, comment this, but it's recommended to re-slice\n# Nozzle temperature PID calibration command:  \"PID_CALIBRATE HEATER=extruder TARGET=245\ncontrol: pid\npid_kp: 26.213\npid_ki:1.304\npid_kd: 131.721\nstep_pulse_duration: 0.000004\n###------------------------------------------------------------------\n[tmc2209 extruder]\nuart_pin:SB2040:gpio15\ninterpolate:False\nrun_current: 0.8\nsense_resistor:0.110\nstealthchop_threshold:0\n##------------------------------------------------------------------\n# [tmc2240 extruder]\n# uart_pin:SB2040:gpio15\n# interpolate:False\n# run_current: 0.65\n# interpolate: False\n# rref: 12300\n# stealthchop_threshold: 0\n\n###------------------------------------------------------------------\n[verify_heater extruder]\nmax_error: 20\ncheck_gain_time:120\nhysteresis: 50\nheating_gain: 2\n\n#####################################################################\n#      FAN\n#####################################################################\n[fan]\npin:SB2040:gpio24\n###------------------------------------------------------------------\n[heater_fan my_hotend_fan]\npin: SB2040:gpio19\nheater: extruder\nheater_temp: 50.0\n\n#####################################################################\n#      TAP && PL08N && BLTOUCH\n#####################################################################\n# [stepper_x]\n# endstop_pin: ^!SB2040:gpio18\n\n# #####################################################################\n# #      TAP && PL08N && BLTOUCH\n# #####################################################################\n# ## https://www.klipper3d.org/Config_Reference.html?h=probe#probe\n# [probe]\n# pin: ^SB2040:gpio22          #gpio22\n# x_offset: 0\n# y_offset: 25.0\n# z_offset: 0\n# speed: 10.0\n# samples: 3\n# samples_result: median\n# sample_retract_dist: 4.0\n# samples_tolerance: 0.010\n# samples_tolerance_retries: 3\n\n"})})]})}function d(e={}){let{wrapper:n}={...(0,i.a)(),...e.components};return n?(0,o.jsx)(n,{...e,children:(0,o.jsx)(p,{...e})}):p(e)}},50065:function(e,n,t){t.d(n,{Z:function(){return a},a:function(){return s}});var r=t(67294);let o={},i=r.createContext(o);function s(e){let n=r.useContext(i);return r.useMemo(function(){return"function"==typeof e?e(n):{...n,...e}},[n,e])}function a(e){let n;return n=e.disableParentContext?"function"==typeof e.components?e.components(o):e.components||o:s(e.components),r.createElement(i.Provider,{value:n},e.children)}}}]);