"use strict";(self.webpackChunkklipper_docs=self.webpackChunkklipper_docs||[]).push([["20980"],{37053:function(e,n,t){t.r(n),t.d(n,{default:()=>_,frontMatter:()=>s,metadata:()=>o,assets:()=>p,toc:()=>l,contentTitle:()=>a});var o=JSON.parse('{"id":"ProductDoc/ToolBoard/fly-sht36/sht36_pro/cfg","title":"Reference Configuration","description":"","source":"@site/i18n/en/docusaurus-plugin-content-docs/current/ProductDoc/ToolBoard/fly-sht36/sht36_pro/cfg.mdx","sourceDirName":"ProductDoc/ToolBoard/fly-sht36/sht36_pro","slug":"/ProductDoc/ToolBoard/fly-sht36/sht36_pro/cfg","permalink":"/fly-docs-next/en/docs/ProductDoc/ToolBoard/fly-sht36/sht36_pro/cfg","draft":false,"unlisted":false,"editUrl":"https://github.com/kluoyun/fly-docs-next/tree/master/docs/ProductDoc/ToolBoard/fly-sht36/sht36_pro/cfg.mdx","tags":[],"version":"current","lastUpdatedBy":"xishangyue","lastUpdatedAt":1746004502000,"sidebarPosition":5,"frontMatter":{"sidebar_position":5,"sidebar_label":"Configuration Reference"},"sidebar":"tutorialSidebar","previous":{"title":"Wiring Reference","permalink":"/fly-docs-next/en/docs/ProductDoc/ToolBoard/fly-sht36/sht36_pro/wiring"},"next":{"title":"Accelerometer Usage","permalink":"/fly-docs-next/en/docs/ProductDoc/ToolBoard/fly-sht36/sht36_pro/adxl"}}'),r=t("85893"),i=t("50065");let s={sidebar_position:5,sidebar_label:"Configuration Reference"},a="Reference Configuration",p={},l=[];function c(e){let n={code:"code",h1:"h1",header:"header",pre:"pre",...(0,i.a)(),...e.components};return(0,r.jsxs)(r.Fragment,{children:[(0,r.jsx)(n.header,{children:(0,r.jsx)(n.h1,{id:"reference-configuration",children:"Reference Configuration"})}),"\n",(0,r.jsx)(n.pre,{children:(0,r.jsx)(n.code,{className:"language-cfg",children:'#####################################################################\n#      Notes\n#####################################################################\n## Please add [include SHT36PRO.cfg] under printer.cfg\n## The tool board will use the following configurations,\n## and if the following configurations are used in other configurations, conflicts may occur.\n## [resonance_tester] && [extruder] && [tmc2209 extruder] && [fan] && [heater_fan my_hotend_fan]\n## [probe] && [bltouch] && [stepper_x]\n\n#####################################################################\n#      Master ID Configuration\n#####################################################################\n[mcu SHT36PRO]\ncanbus_uuid:\n### To query the CAN firmware id: ~/klippy-env/bin/python ~/klipper/scripts/canbus_query.py can0\n### Replace serial with canbus_uuid: and add the id after it\n\n#####################################################################\n#      Temperature monitoring\n#####################################################################\n[temperature_sensor SHT36-PRO]\nsensor_type: temperature_mcu\nsensor_mcu: SHT36PRO\n#--------------------------------------------------------------------\n[temperature_sensor Box]\nsensor_type: ATC Semitec 104GT-2\nsensor_pin: SHT36PRO:gpio27\n#####################################################################\n#      Accelerometer\n#####################################################################\n## https://www.klipper3d.org/Measuring_Resonances.html?h=adxl#adxl345\n[adxl345]\ncs_pin: SHT36PRO:gpio9\nspi_software_sclk_pin: SHT36PRO:gpio10\nspi_software_mosi_pin: SHT36PRO:gpio11\nspi_software_miso_pin: SHT36PRO:gpio12\n#--------------------------------------------------------------------\n[resonance_tester]\naccel_chip: adxl345\nprobe_points: 150, 150, 20\n# Somewhere slightly above the middle of your print bed\nmin_freq: 5\nmax_freq: 133\naccel_per_hz: 75\nhz_per_sec: 1\n\n#####################################################################\n#      Extruder thermal sensitivity\n#####################################################################\n## Please select the type of thermistor you want to use.\n[extruder]\n## Type of sensor - common thermistors are (Generic 3950, ATC Semitec 104GT-2)\nsensor_type: ATC Semitec 104GT-2\nsensor_pin: SHT36PRO:gpio26\n###------------------------------------------------------------------\n## If using PT1000, please connect the jumper next to the thermistor.\n# sensor_type:PT1000\n#pullup_resistor: 1000\n#sensor_pin: SHT36PRO:gpio26\n###------------------------------------------------------------------\n## Using MAX31865 to connect PT100\n#sensor_type: MAX31865\n#sensor_pin: SHT36PRO:gpio1\n#spi_software_sclk_pin: SHT36PRO:gpio10\n#spi_software_mosi_pin: SHT36PRO:gpio11\n#spi_software_miso_pin: SHT36PRO:gpio12\n#rtd_reference_r: 430\n\n#####################################################################\n#      extruder\n#####################################################################\n## https://www.klipper3d.org/Config_Reference.html#extruder\n[extruder]\nstep_pin: SHT36PRO:gpio23\ndir_pin: SHT36PRO:gpio24               # Extruder motor direction pin\nenable_pin: !SHT36PRO:gpio25\nrotation_distance: 21.84\n## rotation_distance = The original rotation_distance multiplied by the actual extrusion length divided by the requested extrusion length.\n## Calibration: 22.44 = old value 22 * actual value 102 / target value 100\ngear_ratio:50:10\n## Gear ratio (Galileo gear ratio 7.5:1, comment this line; BMG is 50:17, output shaft in front, input shaft in back)\nmicrosteps:16\nfull_steps_per_rotation: 200\nnozzle_diameter:0.400\nfilament_diameter:1.75\nheater_pin: SHT36PRO:gpio8\nmin_temp: -230\nmax_temp: 500\nmax_power: 1.0\nmin_extrude_temp: 150\npressure_advance: 0.05\n## Pressure advance\n## https://www.klipper3d.org/Pressure_Advance.html\npressure_advance_smooth_time: 0.040\n#max_extrude_only_distance: 200.0   # If extrusion flow error, comment this, but it\'s recommended to re-slice\n# Nozzle temperature PID calibration command: "PID_CALIBRATE HEATER=extruder TARGET=245"\ncontrol: pid\npid_kp: 26.213\npid_ki:1.304\npid_kd: 131.721\nstep_pulse_duration: 0.000004\n#--------------------------------------------------------------------\n[tmc5160 extruder]\ncs_pin: SHT36PRO:gpio20\nspi_software_sclk_pin: SHT36PRO:gpio10\nspi_software_mosi_pin: SHT36PRO:gpio11\nspi_software_miso_pin: SHT36PRO:gpio12\ninterpolate: False\nrun_current: 0.8\nsense_resistor: 0.075\nstealthchop_threshold: 0\n\n#####################################################################\n#      FAN\n#####################################################################\n[fan]\npin: SHT36PRO:gpio15\n#--------------------------------------------------------------------\n[heater_fan hotend_fan]\npin: SHT36PRO:gpio14\nheater: extruder\nheater_temp: 50.0\n\n#####################################################################\n#      X STOP\n#####################################################################\n# [stepper_x]\n# endstop_pin: !SHT36PRO:gpio21\n## The SHT V2 board has PA1, PA2 two limit pins available, modify configuration according to actual wiring\n## PA1 pin supports high voltage input by default, can be used for 12v proximity sensors, etc. Can be configured as a normal limit pin via jumper\n\n#####################################################################\n#      Stealthburner LED\n#####################################################################\n## https://github.com/VoronDesign/Voron-Stealthburner/tree/main\n[neopixel sb_leds]\npin: SHT36PRO:gpio6\nchain_count: 3\n# Number of LEDs\ncolor_order: GRB\ninitial_RED: 0.4\ninitial_GREEN: 0.8\ninitial_BLUE: 1\ninitial_WHITE: 0.0\n#66CCFF\n\n#####################################################################\n#      TAP && PL08N && BLTOUCH\n#####################################################################\n## https://www.klipper3d.org/Config_Reference.html?h=probe#probe\n# [probe]\n# pin: ^SHT36PRO:gpio3\n# x_offset: 0\n# y_offset: 25.0\n# z_offset: 0\n# speed: 10.0\n# samples: 3\n# samples_result: median\n# sample_retract_dist: 4.0\n# samples_tolerance: 0.010\n# samples_tolerance_retries: 3\n\n#--------------------------------------------------------------------\n## https://www.klipper3d.org/Config_Reference.html?h=probe#bltouch\n#[bltouch]\n#sensor_pin: ^SHT36PRO:gpio3\n#control_pin: SHT36PRO:gpio7\n#x_offset: -26.1\n#y_offset: -15.3\n#z_offset: 2.1\n\n#####################################################################\n#                          Magnetic Angle Sensor\n#####################################################################\n## Magnetic angle sensor\n## For details, see https://www.klipper3d.org/API_Server.html#angledump_angle\n## https://www.klipper3d.org/Debugging.html#motion-analysis-and-data-logging\n# [angle my_angle_sensor]\n# sensor_type: as5047d\n# #sample_period: 0.000400\n# stepper: stepper_x\n# cs_pin: SHT36PRO:gpio17\n# spi_software_sclk_pin: SHT36PRO:gpio18\n# spi_software_mosi_pin: SHT36PRO:gpio19\n# spi_software_miso_pin: SHT36PRO:gpio16\n\n'})})]})}function _(e={}){let{wrapper:n}={...(0,i.a)(),...e.components};return n?(0,r.jsx)(n,{...e,children:(0,r.jsx)(c,{...e})}):c(e)}},50065:function(e,n,t){t.d(n,{Z:function(){return a},a:function(){return s}});var o=t(67294);let r={},i=o.createContext(r);function s(e){let n=o.useContext(i);return o.useMemo(function(){return"function"==typeof e?e(n):{...n,...e}},[n,e])}function a(e){let n;return n=e.disableParentContext?"function"==typeof e.components?e.components(r):e.components||r:s(e.components),o.createElement(i.Provider,{value:n},e.children)}}}]);