"use strict";(self.webpackChunkklipper_docs=self.webpackChunkklipper_docs||[]).push([["86274"],{32316:function(e,n,t){t.r(n),t.d(n,{frontMatter:()=>s,default:()=>p,contentTitle:()=>a,assets:()=>l,toc:()=>c,metadata:()=>o});var o=JSON.parse('{"id":"ProductDoc/ToolBoard/fly-sht36/sht36_v3/cfg","title":"cfg","description":"Please do not modify the configuration while web translation is enabled.","source":"@site/i18n/en/docusaurus-plugin-content-docs/current/ProductDoc/ToolBoard/fly-sht36/sht36_v3/cfg.mdx","sourceDirName":"ProductDoc/ToolBoard/fly-sht36/sht36_v3","slug":"/ProductDoc/ToolBoard/fly-sht36/sht36_v3/cfg","permalink":"/fly-docs-next/en/docs/ProductDoc/ToolBoard/fly-sht36/sht36_v3/cfg","draft":false,"unlisted":false,"editUrl":"https://github.com/kluoyun/fly-docs-next/tree/master/docs/ProductDoc/ToolBoard/fly-sht36/sht36_v3/cfg.mdx","tags":[],"version":"current","lastUpdatedBy":"xishangyue","lastUpdatedAt":1746004502000,"sidebarPosition":5,"frontMatter":{"sidebar_position":5,"sidebar_label":"Configuration Reference"},"sidebar":"tutorialSidebar","previous":{"title":"Wiring Reference","permalink":"/fly-docs-next/en/docs/ProductDoc/ToolBoard/fly-sht36/sht36_v3/wiring"},"next":{"title":"eddy reference configuration","permalink":"/fly-docs-next/en/docs/ProductDoc/ToolBoard/fly-sht36/sht36_v3/cfg2"}}'),r=t(85893),i=t(50065);let s={sidebar_position:5,sidebar_label:"Configuration Reference"},a="Reference Configuration",l={},c=[];function d(e){let n={admonition:"admonition",code:"code",h1:"h1",header:"header",p:"p",pre:"pre",...(0,i.a)(),...e.components};return(0,r.jsxs)(r.Fragment,{children:[(0,r.jsx)(n.admonition,{type:"tip",children:(0,r.jsx)(n.p,{children:"Please do not modify the configuration while web translation is enabled."})}),"\n",(0,r.jsx)(n.admonition,{title:"Note",type:"warning",children:(0,r.jsx)(n.p,{children:"Please do not modify the configuration while web translation is enabled."})}),"\n",(0,r.jsx)(n.admonition,{title:"Notice",type:"danger",children:(0,r.jsx)(n.p,{children:"Please do not modify the configuration while web translation is enabled."})}),"\n",(0,r.jsx)(n.header,{children:(0,r.jsx)(n.h1,{id:"reference-configuration",children:"Reference Configuration"})}),"\n",(0,r.jsx)(n.pre,{children:(0,r.jsx)(n.code,{className:"language-cfg",children:"#####################################################################\n#      Notes\n#####################################################################\n## Please add [include SHT36V3.cfg] under printer.cfg\n## The tool plate will use the following configurations, \n## and if the following configurations are used in other configurations, conflicts may occur.\n## [resonance_tester] && [extruder] && [tmc2209 extruder] && [fan] && [heater_fan my_hotend_fan]\n## [probe] && [bltouch] && [stepper_x]\n\n#####################################################################\n#      Master ID Configuration\n#####################################################################\n[mcu SHT36]  # Tool board serial number\n## CAN ID\n## ~/klippy-env/bin/python ~/klipper/scripts/canbus_query.py can0\ncanbus_uuid: \n#--------------------------------------------------------------------\n# # RS232 ID\n## Please fill in the baud rate according to the firmware configuration.\n# serial: /dev/serial/by-path/platform-1c1c000.usb-usb-0:1.2:1.0-port0\n# baud: 250000\n# restart_method:command\n\n#####################################################################\n#      Temperature monitoring\n#####################################################################\n[temperature_sensor SHT36]\nsensor_type: temperature_mcu\nsensor_mcu: SHT36\n\n#####################################################################\n#      Accelerometer\n#####################################################################\n## https://www.klipper3d.org/Measuring_Resonances.html?h=adxl#adxl345\n[lis2dw]\ncs_pin: SHT36:gpio12\nspi_bus:spi0_gpio4_gpio3_gpio2\n#--------------------------------------------------------------------\n[resonance_tester]\naccel_chip: lis2dw\nprobe_points: 150, 150, 20\n# Somewhere slightly above the middle of your print bed\nmin_freq: 5\nmax_freq: 133\naccel_per_hz: 75\nhz_per_sec: 1 \n\n#####################################################################\n#      Stealthburner LED\n#####################################################################\n## https://github.com/VoronDesign/Voron-Stealthburner/tree/main\n[neopixel sb_leds]\npin: SHT36:gpio26\nchain_count: 3\n# Number of LEDs\ncolor_order: GRB\ninitial_RED: 0.4\ninitial_GREEN: 0.8\ninitial_BLUE: 1\ninitial_WHITE: 0.0\n\n#####################################################################\n#      Extruder thermal sensitivity\n#####################################################################\n## Please select the type of thermocouple you want to use.\n[extruder]\n## Type of sensor - common thermistors are (Generic 3950, ATC Semitec 104GT-2)\nsensor_type:ATC Semitec 104GT-2\nsensor_pin:SHT36:gpio27\n###------------------------------------------------------------------\n## If using PT1000, please connect the jumper next to the thermal sensitivity.\n# sensor_type:PT1000\n# sensor_pin:SHT36:gpio27\n# pullup_resistor: 1000\n###------------------------------------------------------------------\n## Using MAX31865 to connect PT100 or PT1000\n# sensor_type: MAX31865\n# sensor_pin: SHT36:gpio17\n# spi_bus:spi0_gpio4_gpio3_gpio2\n# rtd_reference_r: 430  ## Set to 430 for PT100, 4300 for PT1000\n#rtd_nominal_r: 100  ## Set to 100 for PT100, 1000 for PT1000\n\n#####################################################################\n#      extruder\n#####################################################################\n## https://www.klipper3d.org/Config_Reference.html#extruder\n[extruder]\nstep_pin:SHT36:gpio7\ndir_pin:SHT36:gpio6\nenable_pin: !SHT36:gpio14\nrotation_distance: 21.84\n## rotation_distance = The original rotation_distance multiplied by the actual extrusion length divided by the requested extrusion length.\n## Calibration: 22.44 = old value 22 * actual value 102 / target value 100\ngear_ratio:50:10\n## Gear ratio (Galileo 7.5:1 and comment this line; BMG is 50:17, output shaft front, input shaft rear)\nmicrosteps:16\nfull_steps_per_rotation: 200   \nnozzle_diameter:0.400\nfilament_diameter:1.75\nheater_pin:SHT36:gpio23\nmin_temp:-200\nmax_temp:350\nmax_power:1.0\nmin_extrude_temp:-200\npressure_advance: 0.05\npressure_advance_smooth_time: 0.040\n#max_extrude_only_distance: 200.0   # If extrusion flow error, comment this, but it's recommended to re-slice\n# Nozzle temperature PID calibration command: \"PID_CALIBRATE HEATER=extruder TARGET=245\ncontrol: pid\npid_kp: 26.213\npid_ki:1.304\npid_kd: 131.721\n###------------------------------------------------------------------\n[tmc2209 extruder]\nuart_pin:SHT36:gpio15\ninterpolate:False\nrun_current: 0.8\nsense_resistor:0.110\nstealthchop_threshold:0\n###------------------------------------------------------------------\n[verify_heater extruder]\nmax_error: 20\ncheck_gain_time:120\nhysteresis: 50\nheating_gain: 2\n\n#####################################################################\n#      FAN\n#####################################################################\n[fan]\npin:SHT36:gpio21\n###------------------------------------------------------------------\n[heater_fan my_hotend_fan]\npin: SHT36:gpio13\nheater: extruder\nheater_temp: 50.0\n\n#####################################################################\n#      TAP && PL08N && BLTOUCH \n#####################################################################\n# [stepper_x]\n# endstop_pin: ^!SHT36:gpio16\n\n#####################################################################\n#      TAP && PL08N && BLTOUCH \n#####################################################################\n## https://www.klipper3d.org/Config_Reference.html?h=probe#probe\n# [probe]\n# pin: ^SHT36:gpio22\n# x_offset: 0\n# y_offset: 25.0\n# z_offset: 0\n# speed: 10.0\n# samples: 3\n# samples_result: median\n# sample_retract_dist: 4.0\n# samples_tolerance: 0.010\n# samples_tolerance_retries: 3\n#--------------------------------------------------------------------\n## https://www.klipper3d.org/Config_Reference.html?h=probe#bltouch\n#[bltouch]\n#sensor_pin: ^SHT36:gpio22    # Signal interface\n#control_pin: SHT36:gpio24    # Servo control\n#x_offset: -26.1              # X-axis sensor offset relative to nozzle, determine offset yourself\n#y_offset: -15.3              # Y-axis sensor offset relative to nozzle, determine offset yourself\n#z_offset: 2.1                # Z-axis sensor offset relative to nozzle, determine offset yourself\n\n"})})]})}function p(e={}){let{wrapper:n}={...(0,i.a)(),...e.components};return n?(0,r.jsx)(n,{...e,children:(0,r.jsx)(d,{...e})}):d(e)}},50065:function(e,n,t){t.d(n,{Z:()=>a,a:()=>s});var o=t(67294);let r={},i=o.createContext(r);function s(e){let n=o.useContext(i);return o.useMemo(function(){return"function"==typeof e?e(n):{...n,...e}},[n,e])}function a(e){let n;return n=e.disableParentContext?"function"==typeof e.components?e.components(r):e.components||r:s(e.components),o.createElement(i.Provider,{value:n},e.children)}}}]);