"use strict";(self.webpackChunkklipper_docs=self.webpackChunkklipper_docs||[]).push([["66212"],{15834:function(n,e,t){t.r(e),t.d(e,{default:()=>d,frontMatter:()=>s,metadata:()=>r,assets:()=>p,toc:()=>_,contentTitle:()=>a});var r=JSON.parse('{"id":"ProductDoc/MainBoard/fly-super/fly-super8/cfg","title":"Klipper Reference Configurations","description":"Voron Trident Example Configuration","source":"@site/i18n/en/docusaurus-plugin-content-docs/current/ProductDoc/MainBoard/fly-super/fly-super8/cfg.mdx","sourceDirName":"ProductDoc/MainBoard/fly-super/fly-super8","slug":"/ProductDoc/MainBoard/fly-super/fly-super8/cfg","permalink":"/fly-docs-next/en/docs/ProductDoc/MainBoard/fly-super/fly-super8/cfg","draft":false,"unlisted":false,"editUrl":"https://github.com/kluoyun/fly-docs-next/tree/master/docs/ProductDoc/MainBoard/fly-super/fly-super8/cfg.mdx","tags":[],"version":"current","lastUpdatedBy":"xishangyue","lastUpdatedAt":1746004502000,"sidebarPosition":5,"frontMatter":{"sidebar_position":5,"sidebar_label":"Klipper Reference Configurations"},"sidebar":"tutorialSidebar","previous":{"title":"Main Board Wiring","permalink":"/fly-docs-next/en/docs/ProductDoc/MainBoard/fly-super/fly-super8/wiring"},"next":{"title":"CAN Bridge","permalink":"/fly-docs-next/en/docs/ProductDoc/MainBoard/fly-super/fly-super8/canbridge"}}'),o=t("85893"),i=t("50065");let s={sidebar_position:5,sidebar_label:"Klipper Reference Configurations"},a="Klipper Reference Configurations",p={},_=[{value:"Voron Trident Example Configuration",id:"voron-trident-example-configuration",level:2},{value:"vzboot\u793A\u4F8B\u914D\u7F6E",id:"vzboot\u793A\u4F8B\u914D\u7F6E",level:2}];function c(n){let e={code:"code",h1:"h1",h2:"h2",header:"header",pre:"pre",...(0,i.a)(),...n.components};return(0,o.jsxs)(o.Fragment,{children:[(0,o.jsx)(e.header,{children:(0,o.jsx)(e.h1,{id:"klipper-reference-configurations",children:"Klipper Reference Configurations"})}),"\n",(0,o.jsx)(e.h2,{id:"voron-trident-example-configuration",children:"Voron Trident Example Configuration"}),"\n",(0,o.jsx)(e.pre,{children:(0,o.jsx)(e.code,{className:"language-cfg",children:'####################################################################################\n#                         3D MELLOW /FLY-Super8-Pro                                #\n####################################################################################\n## FLY-Super8-Pro Documentation: http://mellow.klipper.cn/#/board/fly_super8_pro/README\n## FLY-Super8-Pro Schematic: https://github.com/Mellow-3D/Fly-Super8Pro\n## FLY Official Taobao Store: https://shop126791347.taobao.com/shop/view_shop.htm?spm=a230r.1.14.4.1a4840a8hyvpPJ&user_number_id=2464680006\n## For after-sales, please contact Taobao customer service\n## FLY Technical Support Group: 621032883\n## FLY-RRF Firmware Group: 786561979\n\n#########################################################################\n#                                Notes                                  #\n#########################################################################\n##***Items to change/check:***\n## MCU path                              [mcu]\n## Printer movement range                 xyz position\n## Thermistor type                        [extruder] and [heater_bed]\n## Z endstop stop position                [homing_override]\n## Z endstop offset position              [stepper_z]\n## PID tuning                             [extruder] and [heater_bed]\n## Fine-tune extruder stepper value       [extruder]\n\n#########################################################################\n#                              File Includes                            #\n#########################################################################\n#[include fluidd.cfg]          # FLUIDD include file.\n#[include mainsail.cfg]        # MAINSAIL include file.\n#Choose which file to use as needed\n\n#########################################################################\n#                             Mainboard Config                          #\n#########################################################################\n[mcu]                           # FLY board ID\nserial: /dev/serial/by-id/usb-Klipper_stm32f407xx_XXXXXXXXXXXXXXXXXXXXX\n### To find USB firmware id: ls -l /dev/serial/by-id/\n### Replace /dev/serial/by-id/usb-Klipper_stm32f407xx_XXXXXXXXXXXXXXXXXXXXX with your id\n#canbus_uuid: e51d5c71a901\n### To find CAN firmware id: ~/klippy-env/bin/python ~/klipper/scripts/canbus_query.py can0\n### For CAN, replace serial with canbus_uuid: and add the id\n\n#########################################################################\n#                         Printer Model and Acceleration                #\n#########################################################################\n[printer]                       # Printer settings\nkinematics: corexy              # Kinematics: cartesian, corexy, etc.\nmax_velocity: 300               # Max printer speed\nmax_accel: 3000                 # Max acceleration (max 3000)\nmax_z_velocity: 15              # Max Z speed\nmax_z_accel: 100                # Max Z acceleration\nsquare_corner_velocity: 5.0     # Square corner velocity, lower helps avoid inertia from heavy beds\n\n#########################################################################\n#                             Temperature Monitoring                    #\n#########################################################################\n[temperature_sensor Fly-Super8]     # FLY board temperature (rename as needed)\nsensor_type: temperature_mcu     # Linked to MCU (default)\n#--------------------------------------------------------------------\n[temperature_sensor FLY-\u03C0]        # Host temperature\nsensor_type: temperature_host     # Linked to host\n#--------------------------------------------------------------------\n# [temperature_sensor Box]          # Chamber temperature (requires extra sensor, see example)\n# sensor_type: ATC Semitec 104GT-2  # Sensor model\n# sensor_pin: PA4                   # Signal pin\n\n#########################################################################\n#                           Bed Mesh Calibration                        #\n#########################################################################\n[bed_mesh]\nspeed: 50                    # Calibration speed\nhorizontal_move_z: 5         # Z lift before moving to next probe point\nmesh_min: 30,30              # Min calibration point x,y\nmesh_max: 270, 270           # Max calibration point x,y\nprobe_count: 4,4             # Probe points (4x4 = 16 points)\nmesh_pps: 2,2                # Extra probe points\nalgorithm: bicubic           # Algorithm\nbicubic_tension: 0.2         # Bicubic tension (do not change)\n\n#########################################################################\n#             X/Y Stepper Motor Settings                                #\n#########################################################################\n#   B Motor ---- Motor A\n#   |                  |\n#   |------Extruder-----|\n#   |                  |\n#   |                  |\n#          Front\n#########################################################################\n#                  X Stepper Motor on Driver0 (B Motor)                 #\n#########################################################################\n[stepper_x]\nstep_pin: PE2                       # X step pin\ndir_pin: PC5                        # X direction pin, add ! to reverse\nenable_pin: !PF11                   # X enable pin, add ! or motor won\'t work\nrotation_distance: 40               # Pulley circumference mm (2GT-20T: 40, 2GT-16T: 32)\nmicrosteps: 16                      # Microsteps, higher = better quality, more load\nfull_steps_per_rotation: 200        # Steps per revolution (1.8\xb0: 200, 0.9\xb0: 400)\nendstop_pin: !PG12                  # Endstop pin, recommend NC with ! to invert, at io-3\n### NC triggers on disconnect, ! inverts, prevents crashes if wire breaks\nposition_min: 0                     # Min soft limit\nposition_endstop: 300               # Max soft limit (250/300/350mm)\nposition_max: 300                   # Max mechanical limit (250/300/350mm)\nhoming_speed: 50                    # Homing speed, max 100, don\'t set too high\nhoming_retract_dist: 5              # Retract distance after first endstop trigger\nstep_pulse_duration: 0.000004\n#--------------------------------------------------------------------\n##  Ensure correct driver type (2208 or 2209)\n[tmc2209 stepper_x]                 # X driver config - TMC2209\nuart_pin: PC4                       # UART pin\ninterpolate: False                  # 256 microstep interpolation (True/False)\nrun_current: 0.8                    # Motor run current (A)\nsense_resistor: 0.110               # Do not change\nstealthchop_threshold: 500          # StealthChop threshold (set 0 to disable)\n#--------------------------------------------------------------------\n#[tmc5160 stepper_x]                 # X driver config - TMC5160\n#cs_pin: PC4                         # SPI CS pin\n# spi_software_mosi_pin: PB5\n# spi_software_miso_pin: PB4\n# spi_software_sclk_pin: PB3\n#run_current: 1.0                    # Motor run current\n#interpolate: False                  # 256 microstep interpolation (True/False)\n#sense_resistor: 0.075               # Do not change (0.033 for 5160 Pro)\n#stealthchop_threshold: 500          # StealthChop threshold (set 0 to disable)\n#########################################################################\n#                  Y Stepper Motor on Driver1 (A Motor)                 #\n#########################################################################\n[stepper_y]\nstep_pin: PE3                       # Y step pin\ndir_pin: PF13                       # Y direction pin, add ! to reverse\nenable_pin: !PF14                   # Y enable pin, add ! or motor won\'t work\nrotation_distance: 40               # Pulley circumference mm (2GT-20T: 40, 2GT-16T: 32)\nmicrosteps: 16                      # Microsteps, higher = better quality, more load\nfull_steps_per_rotation: 200        # Steps per revolution (1.8\xb0: 200, 0.9\xb0: 400)\nendstop_pin: !PG11                  # Endstop pin, recommend NC with ! to invert, at io-0\n### NC triggers on disconnect, ! inverts, prevents crashes if wire breaks\nposition_min: 0                     # Min soft limit\nposition_endstop: 300               # Max soft limit (250/300/350mm)\nposition_max: 300                   # Max mechanical limit (250/300/350mm)\nhoming_speed: 50                    # Homing speed, max 100, don\'t set too high\nhoming_retract_dist: 5              # Retract distance after first endstop trigger\nstep_pulse_duration: 0.000004\n#--------------------------------------------------------------------\n##  Ensure correct driver type (2208 or 2209)\n[tmc2209 stepper_y]                 # Y driver config - TMC2209\nuart_pin: PF12                      # UART pin\ninterpolate: False                  # 256 microstep interpolation (True/False)\nrun_current: 0.8                    # Motor run current (A)\nsense_resistor: 0.110               # Do not change\nstealthchop_threshold: 500          # StealthChop threshold (set 0 to disable)\n#--------------------------------------------------------------------\n#[tmc5160 stepper_y]                 # Y driver config - TMC5160\n#cs_pin: PF12                        # SPI CS pin\n# spi_software_mosi_pin: PB5\n# spi_software_miso_pin: PB4\n# spi_software_sclk_pin: PB3\n#run_current: 1.0                    # Motor run current\n#interpolate: False                  # 256 microstep interpolation (True/False)\n#sense_resistor: 0.075               # Do not change (0.033 for 5160 Pro)\n#stealthchop_threshold: 500          # StealthChop threshold (set 0 to disable)\n\n#########################################################################\n#                     Z Stepper Motor Settings                          #\n#########################################################################\n#   |----- Z1 -----|\n#   |              |\n#   |              |\n#   |              |\n#   Z0----LCD----Z2\n\n#########################################################################\n#                           Z Stepper Motor                             #\n#########################################################################\n##  Z Stepper - Front Left on Driver3\n[stepper_z]\nstep_pin: PE14                 # Z step pin\ndir_pin:  !PE8                 # Z direction pin, add ! to reverse\nenable_pin: !PE9               # Z enable pin, add ! or motor won\'t work\n### T8x4 leadscrew. One turn moves bed 4mm\n## Adjust according to your leadscrew\nrotation_distance: 8           # Leadscrew pitch (adjust as needed, 3 places)\nfull_steps_per_rotation: 200   # Steps per revolution (1.8\xb0: 200, 0.9\xb0: 400)\nmicrosteps: 16                 # Microsteps, higher = better quality, more load\nendstop_pin: !PG10             # Endstop pin, recommend NC with ! to invert, at io-1\n## position_endstop is nozzle position relative to bed (Z0) at endstop trigger (mm)\nposition_endstop: -0.5\n## Positive = above bed, negative = below bed\n## Increase position_endstop to move nozzle closer to bed\n## After running Z_ENDSTOP_CALIBRATE, value is stored at end of config\nposition_min: -15              # (for nozzle cleaning, set to around -5)\nposition_max: 300              # Max mechanical limit (250/300/350mm)\nhoming_speed: 5                # Homing speed, max 20\nsecond_homing_speed: 3         # Second homing speed, max 10\nhoming_retract_dist: 0         # Retract distance\nstep_pulse_duration: 0.000004\n#--------------------------------------------------------------------\n[tmc2209 stepper_z]\nuart_pin: PE7                 # UART pin\ninterpolate: false            # 256 microstep interpolation\nrun_current: 0.8              # Run current (A)\nsense_resistor: 0.110         # Do not change\nstealthchop_threshold: 500    # StealthChop threshold (set 0 to disable)\n#--------------------------------------------------------------------\n#[tmc5160 stepper_z]                 # Z driver config - TMC5160\n#cs_pin: PE7                         # SPI CS pin\n# spi_software_mosi_pin: PB5\n# spi_software_miso_pin: PB4\n# spi_software_sclk_pin: PB3\n#run_current: 1.0                    # Motor run current\n#interpolate: False                  # 256 microstep interpolation (True/False)\n#sense_resistor: 0.075               # Do not change (0.033 for 5160 Pro)\n#stealthchop_threshold: 500          # StealthChop threshold (set 0 to disable)\n#--------------------------------------------------------------------\n\n##  Z1 Stepper - Rear on Driver4\n[stepper_z1]\nstep_pin: PE15                # Z1 step pin\ndir_pin:  !PE11               # Z1 direction pin, add ! to reverse\nenable_pin: !PF2              # Z1 enable pin, add ! or motor won\'t work\n### T8x4 leadscrew. One turn moves bed 4mm\n## Adjust according to your leadscrew\nrotation_distance: 8          # Leadscrew pitch\nfull_steps_per_rotation: 200  # Steps per revolution (1.8\xb0: 200, 0.9\xb0: 400)\nmicrosteps: 16                # Microsteps, higher = better quality, more load\nstep_pulse_duration: 0.000004\n#--------------------------------------------------------------------\n[tmc2209 stepper_z1]\nuart_pin: PE10                # UART pin\ninterpolate: false            # 256 microstep interpolation\nrun_current: 0.8              # Run current (A)\nsense_resistor: 0.110         # Do not change\nstealthchop_threshold: 500    # StealthChop threshold (set 0 to disable)\n#--------------------------------------------------------------------\n#[tmc5160 stepper_z1]                # Z1 driver config - TMC5160\n#cs_pin: PE10                        # SPI CS pin\n# spi_software_mosi_pin: PB5\n# spi_software_miso_pin: PB4\n# spi_software_sclk_pin: PB3\n#run_current: 1.0                    # Motor run current\n#interpolate: False                  # 256 microstep interpolation (True/False)\n#sense_resistor: 0.075               # Do not change (0.033 for 5160 Pro)\n#stealthchop_threshold: 500          # StealthChop threshold (set 0 to disable)\n#--------------------------------------------------------------------\n\n##  Z2 Stepper - Front Right on Driver5\n[stepper_z2]\nstep_pin: PE1                 # Z2 step pin\ndir_pin: !PF0                 # Z2 direction pin, add ! to reverse\nenable_pin: !PC15             # Z2 enable pin, add ! or motor won\'t work\n### T8x4 leadscrew. One turn moves bed 4mm\n## Adjust according to your leadscrew\nrotation_distance: 8          # Leadscrew pitch\nfull_steps_per_rotation: 200  # Steps per revolution (1.8\xb0: 200, 0.9\xb0: 400)\nmicrosteps: 16                # Microsteps, higher = better quality, more load\nstep_pulse_duration: 0.000004\n#--------------------------------------------------------------------\n[tmc2209 stepper_z2]\nuart_pin: PF1                # UART pin\ninterpolate: false            # 256 microstep interpolation\nrun_current: 0.8              # Run current (A)\nsense_resistor: 0.110         # Do not change\nstealthchop_threshold: 500    # StealthChop threshold (set 0 to disable)\n#--------------------------------------------------------------------\n#[tmc5160 stepper_z2]                # Z2 driver config - TMC5160\n#cs_pin: PF1                         # SPI CS pin\n# spi_software_mosi_pin: PB5\n# spi_software_miso_pin: PB4\n# spi_software_sclk_pin: PB3\n#run_current: 1.0                    # Motor run current\n#interpolate: False                  # 256 microstep interpolation (True/False)\n#sense_resistor: 0.075               # Do not change (0.033 for 5160 Pro)\n#stealthchop_threshold: 500          # StealthChop threshold (set 0 to disable)\n#--------------------------------------------------------------------\n\n#########################################################################\n#                     E0 Extruder Settings                              #\n#########################################################################\n#Driver2\n[extruder]                          # Extruder\nstep_pin: PE4                       # Step pin\ndir_pin: !PG0                       # Direction pin\nenable_pin: !PG1                    # Enable pin\n## Update the following values after extruder calibration\n## For example, if you request 100mm but get 102mm:\n## rotation_distance = <old value> * <actual length> / <requested length>\n## Example: 22.44 = 22 * 102 / 100\nrotation_distance: 22.44            # Step value\ngear_ratio: 50:17                   # Gear ratio (Galileo 7.5:1, comment out; BMG 50:17, output shaft front, input shaft rear)\nmicrosteps: 16                      # Microsteps, higher = better quality, more load\nfull_steps_per_rotation: 200        # Steps per revolution (200 for 1.8\xb0, 400 for 0.9\xb0)\nnozzle_diameter: 0.400              # Nozzle diameter\nfilament_diameter: 1.75             # Filament diameter\nheater_pin: PB0                     # Heater pin\nsensor_type: ATC Semitec 104GT-2    # Sensor type (Generic 3950, ATC Semitec 104GT-2, PT1000)\nsensor_pin: PF4                     # Sensor pin (ADC_0)\nmin_temp: 10                        # Min temp (exceeding triggers emergency stop)\nmax_temp: 350                       # Max temp (exceeding triggers emergency stop)\nmax_power: 1.0                      # Max power\nmin_extrude_temp: 170               # Min extrusion temp\npressure_advance: 0.05              # Pressure advance (keep below 1.0)\n#Pressure advance tuning: https://www.klipper3d.org/Pressure_Advance.html\npressure_advance_smooth_time: 0.040 # Smoothing time (default 0.040)\n#max_extrude_only_distance: 200.0   # Comment out if extrusion error, but recommend reslicing\n#Nozzle PID tune: "PID_CALIBRATE HEATER=extruder TARGET=245"\ncontrol = pid                # PID control (commented after tuning)\npid_kp = 26.213              # PID Kp (commented after tuning)\npid_ki = 1.304               # PID Ki (commented after tuning)\npid_kd = 131.721             # PID Kd (commented after tuning)\nstep_pulse_duration: 0.000004\n#--------------------------------------------------------------------\n[tmc2209 extruder]                  # Extruder driver config - TMC2209\nuart_pin: PF15                      # UART pin\ninterpolate: False                  # 256 microstep interpolation (True/False)\nrun_current: 0.5                    # Run current (A)\nsense_resistor: 0.110               # Do not change\nstealthchop_threshold: 500          # StealthChop threshold (set 0 to disable)\n#--------------------------------------------------------------------\n#[tmc5160 extruder]                  # Extruder driver config - TMC5160\n#cs_pin: PF15                        # SPI CS pin\n# spi_software_mosi_pin: PB5\n# spi_software_miso_pin: PB4\n# spi_software_sclk_pin: PB3\n#run_current: 1.0                    # Motor run current\n#interpolate: False                  # 256 microstep interpolation (True/False)\n#sense_resistor: 0.075               # Do not change (0.033 for 5160 Pro)\n#stealthchop_threshold: 500          # StealthChop threshold (set 0 to disable)\n\n#########################################################################\n#                            Heated Bed Config                          #\n#########################################################################\n[heater_bed]\nheater_pin: PE5              # Bed heater pin\nsensor_type: Generic 3950    # Bed sensor type\nsensor_pin:  PC1             # Bed sensor pin\nmax_power: 0.5               # Bed power\nmin_temp: 0                  # Min temp (exceeding triggers emergency stop)\nmax_temp: 120                # Max temp (exceeding triggers emergency stop)\n# Bed PID tune: "PID_CALIBRATE HEATER=heater_bed TARGET=100"\ncontrol: pid                # PID control (commented after tuning)\npid_kp: 58.437              # PID Kp (commented after tuning)\npid_ki: 2.347               # PID Ki (commented after tuning)\npid_kd: 363.769             # PID Kd (commented after tuning)\n\n#########################################################################\n#                            Fan Config                                 #\n#########################################################################\n[fan]                        # Print cooling fan\npin: PA0                     # Signal pin\nkick_start_time: 0.5         # Startup time (do not change)\noff_below: 0.10              # Do not change\n#--------------------------------------------------------------------\n[heater_fan hotend_fan]      # Hotend fan\npin: PA1                     # Signal pin\nmax_power: 1.0               # Max speed\nkick_start_time: 0.5         # Startup time (do not change)\nheater: extruder             # Linked device: extruder\nheater_temp: 50              # Fan turns on at this temp\nfan_speed: 1.0               # Fan speed\n#--------------------------------------------------------------------\n[heater_fan controller_fan]  # Electronics bay fan\npin: PA2                     # Signal pin\nmax_power: 1.0               # Max speed\nkick_start_time: 0.5         # Startup time (do not change)\nheater: heater_bed           # Linked device: bed\nheater_temp: 50              # Fan turns on at this temp\nfan_speed: 1.0               # Fan speed\n#--------------------------------------------------------------------\n[heater_fan exhaust_fan]    # Exhaust fan\npin: PA3                     # Signal pin\nmax_power: 1.0               # Max speed\nkick_start_time: 0.5         # Startup time (do not change)\nheater: heater_bed           # Linked device: bed\nheater_temp: 70              # Fan turns on at this temp\nfan_speed: 1.0               # Fan speed\n\n#########################################################################\n#                           Bed Leveling Probe                          #\n#########################################################################\n# Default PL08N probe should not be lower than nozzle, only for leveling.\n# If your PL08N is NO (normally open), add ! to pin.\n[probe]\npin: ^PF3                   # Endstop pin, recommend NC with ! to invert, at io-2\nx_offset: 0                  # X offset from nozzle\ny_offset: 25.0               # Y offset from nozzle\nz_offset: 0                  # Z offset from nozzle\nspeed: 10.0                  # Probing speed\nsamples: 3                   # Number of samples\nsamples_result: median       # Sample method (median)\nsample_retract_dist: 4.0     # Probe retract distance\nsamples_tolerance: 0.006     # Sample tolerance (too small may increase retries)\nsamples_tolerance_retries: 3 # Retries if out of tolerance\n\n#########################################################################\n#                               Homing                                  #\n#########################################################################\n# [safe_z_home]                # Z homing coordinates\n# home_xy_position:206,300     # Z homing position (adjust as needed)\n# speed:100                    # Homing speed\n# z_hop:10                     # Z lift before homing\n#--------------------------------------------------------------------\n[homing_override]                   # Z homing macro (comment out if using Klicky)\naxes: z\nset_position_z: 0\ngcode:\nG90\nG0 Z5 F600\nG28 X Y\n## Z endstop position\n## After testing, update X and Y to your values (e.g. X157, Y305)\n## Endstop position definition step.\nG0 X-10 Y-10 F3600               # Update X and Y values here\n\nG28 Z\nG0 Z10 F1800\n\n    ##  Delete the "#" at the start of the line for your bed size:\n    ##  Roughly measure to center of your bed.\n#--------------------------------------------------------------------\n    ##  For 250mm bed, uncomment below to enable\n    #G0 X125 Y125 Z30 F3600\n#--------------------------------------------------------------------\n    ##  For 300mm bed, uncomment below to enable\n    #G0 X150 Y150 Z30 F3600\n#--------------------------------------------------------------------\n    ##  For 350mm bed, uncomment below to enable\n    #G0 X175 Y175 Z30 F3600\n#--------------------------------------------------------------------\n\n#########################################################################\n#   3Z Bed Tilt Adjustment\n#########################################################################\n[z_tilt]\n##  Use Z_TILT_ADJUST command to level bed\n##  z_positions: printhead positions\n\n##--------------------------------------------------------------------\n##  For 250mm bed, uncomment below to enable\n#z_positions:\n#   -50, 18\n#   125, 298\n#   300, 18\n#points:\n#   30, 5\n#   125, 195\n#   220, 5\n##--------------------------------------------------------------------\n##  For 300mm bed, uncomment below to enable\n#z_positions:\n#   -50, 18\n#   150, 348\n#   350, 18\n#points:\n#   30, 5\n#   150, 245\n#   270, 5\n##--------------------------------------------------------------------\n##  For 350mm bed, uncomment below to enable\n#z_positions:\n#   -50, 18\n#   175, 398\n#   400, 18\n#points:\n#   30, 5\n#   175, 295\n#   320, 5\n#--------------------------------------------------------------------\nspeed: 100                   # Leveling speed\nhorizontal_move_z: 10        # Z lift before move\nretry_tolerance: 0.0075      # Probe tolerance\nretries: 5                   # Retries if out of tolerance\n\n#########################################################################\n#                      FLY-Mini 12864 LCD\n#########################################################################\n[board_pins]\naliases:\n    EXP1_1=PE12, EXP1_3=PB2,  EXP1_5=PC14,   EXP1_7=PG14,  EXP1_9=<GND>,\n    EXP1_2=PE13, EXP1_4=PG8,  EXP1_6=PC13,   EXP1_8=PG13,  EXP1_10=<5V>,\n    # EXP2 header\n    EXP2_1=PA6, EXP2_3=PB7,  EXP2_5=PB6,  EXP2_7=PG15,   EXP2_9=<GND>,\n    EXP2_2=PA5, EXP2_4=PA4,  EXP2_6=PA7,  EXP2_8=<RST>,  EXP2_10=<NC>,\n#--------------------------------------------------------------------\n#[display]\n#lcd_type: uc1701                # LCD driver type\n#cs_pin: EXP1_3                  # LCD CS pin\n#a0_pin: EXP1_4                  # LCD A0 pin\n#rst_pin: EXP1_5                 # LCD reset pin\n#contrast: 63                    # Contrast\n#encoder_pins: ^EXP2_5, ^!EXP2_3 # Encoder pins\n#click_pin: ^!EXP1_2             # Encoder click pin\n#spi_bus: spi1                   # SPI bus\n#--------------------------------------------------------------------\n##### For FLY Mini12864\n#[neopixel fly_mini12864]\n#pin: EXP1_6                     # LCD backlight pin\n#chain_count: 3\n#initial_RED: 0.5                # Red LED brightness (0-1)\n#initial_GREEN: 0.5              # Green LED brightness (0-1)\n#initial_BLUE: 0.5               # Blue LED brightness (0-1)\n#color_order: RGB                # Color order\n\n#########################################################################\n#                          Idle Timeout (Bed Off)\n#########################################################################\n[idle_timeout]\ntimeout: 1800                # Turn off bed after 30 minutes idle\n\n#########################################################################\n#                           Custom gcode Macros                         #\n#########################################################################\n[gcode_macro PRINT_START]          # Macro for print start, customize pre-print actions\ngcode:\n    G92 E0                         # Reset extruder\n    BED_MESH_CLEAR                 # Unload bed mesh\n    G28                            # Home all axes\n    Z_TILT_ADJUST                  # Gantry leveling\n    G28                            # Home all axes\n    G1 Z20 F3000                   # Move nozzle away from bed\n    BED_MESH_PROFILE LOAD=default  # Load bed mesh\n#--------------------------------------------------------------------\n[gcode_macro PRINT_HUAXIAN]        # Macro for pre-print actions after PRINT_START\ngcode:\n    G1 Z5 F3000 \n    G92 E0;              # Reset extruder\n    G90                  # Absolute coordinates\n    G0 X5 Y1 F6000       # Move to x5 y1 at 100mm/s (F6000 = 6000mm/min)\n    G0 Z0.4              # Move Z to 0.4\n    G91                  # Relative coordinates\n    G1 X100 E20 F1200;   # Move X 100mm and extrude, adjust F for extrusion rate\n    G1 Y1                # Move Y 1mm\n    G1 X-100 E20 F1200;  # Move X -100mm and extrude, adjust F for extrusion rate\n    G0 z5                # Raise Z 5mm\n    G1 E-5.0 F3600       # Retract filament\n    G92 E0;              # Reset extruder\n    G90                  # Absolute coordinates\n\n#--------------------------------------------------------------------\n[gcode_macro PRINT_END]            # Macro for print end, customize post-print actions\ngcode:\n    #   Get Boundaries\n    {% set max_x = printer.configfile.config["stepper_x"]["position_max"]|float %}\n    {% set max_y = printer.configfile.config["stepper_y"]["position_max"]|float %}\n    {% set max_z = printer.configfile.config["stepper_z"]["position_max"]|float %}\n    \n    #   Check end position to determine safe directions to move\n    {% if printer.toolhead.position.x < (max_x - 20) %}\n        {% set x_safe = 20.0 %}\n    {% else %}\n        {% set x_safe = -20.0 %}\n    {% endif %}\n\n    {% if printer.toolhead.position.y < (max_y - 20) %}\n        {% set y_safe = 20.0 %}\n    {% else %}\n        {% set y_safe = -20.0 %}\n    {% endif %}\n\n    {% if printer.toolhead.position.z < (max_z - 2) %}\n        {% set z_safe = 2.0 %}\n    {% else %}\n        {% set z_safe = max_z - printer.toolhead.position.z %}\n    {% endif %}\n    \n    M400                              # Wait for buffer to clear\n    G92 E0                            # Reset extruder\n    G1 E-10.0 F3600                   # Retract filament\n    G91                               # Relative positioning\n    G0 Z{z_safe} F3600                # Raise gantry\n    G0 X{x_safe} Y{y_safe} F20000     # Move nozzle to remove stringing\n    M104 S0                           # Turn off hotend\n    M140 S0                           # Turn off bed\n    M106 S0                           # Turn off print fan\n    G90                               # Absolute positioning\n    G0 X{max_x / 2} Y{max_y} F3600    # Move nozzle to rear\n    BED_MESH_CLEAR                    # Unload bed mesh\n\n'})}),"\n",(0,o.jsx)(e.h2,{id:"vzboot\u793A\u4F8B\u914D\u7F6E",children:"vzboot\u793A\u4F8B\u914D\u7F6E"}),"\n",(0,o.jsx)(e.pre,{children:(0,o.jsx)(e.code,{className:"language-cfg",children:'### For Fly Super PRO 8 ####\n### And Mellow Kit  ####\n### For Fly Super non pro, the SPI bus is spi3\n[include mainsail.cfg]\n[include timelapse.cfg]\n#[include PIS.cfg] this is the fysetc accelerometer\n#[include crampon.cfg] This is the Annex Crampon accelerometer\n\n##### VzBoT############\n#######################\n\n[printer]\nkinematics: corexy\nmax_velocity: 2500\nmax_accel: 10000\n#max_accel_to_decel: 5000\nmax_z_velocity: 20\nmax_z_accel: 1500\nsquare_corner_velocity: 10\n\n########################\n########################\n\n\n[mcu]\nserial:/dev/serial/by-id/[replace with your own board ID] \n#restart_method: command\n\n[mcu rpi]\nserial: /tmp/klipper_host_mcu\n\n#[adxl345] (if you run sensor connected to Pi)\n#cs_pin: rpi:None\n\n#[resonance_tester] This is for accel sensor connected to Pi\n#accel_chip: adxl345\n#probe_points:\n#    155,155,20  # an example\n\n########################\n########################\n\n[stepper_x]\n##	in M3 position\nstep_pin: PE14\ndir_pin: PE8\nenable_pin: !PE9\nrotation_distance: 40\nmicrosteps: 16\nfull_steps_per_rotation: 200  \nendstop_pin: ^PG11\nposition_endstop: -19\nposition_min: -19\nposition_max: 315\nhoming_speed: 35  \nhoming_retract_dist: 0\n[tmc5160 stepper_x] \nspi_bus: spi1a \n#spi_software_mosi_pin: PB5\n#spi_software_miso_pin: PB4\n#spi_software_sclk_pin: PB3\ncs_pin: PE7\ninterpolate: false\nrun_current: 1.75\nsense_resistor: 0.033\ndriver_TBL: 0\ndriver_TOFF: 1\n\n\n[stepper_x1]\n##	in M5 position\nstep_pin: PE1\ndir_pin: PF0\nenable_pin: !PC15\nrotation_distance: 40\nmicrosteps: 16\nfull_steps_per_rotation: 200\n[tmc5160 stepper_x1] \nspi_bus: spi1a\n#spi_software_mosi_pin: PB5\n#spi_software_miso_pin: PB4\n#spi_software_sclk_pin: PB3\ncs_pin: PF1\ninterpolate: false\nrun_current: 1.75\nsense_resistor: 0.033\ndriver_TBL: 0\ndriver_TOFF: 1\n\n\n[stepper_y]\n##	in M4 position\nstep_pin: PE15\ndir_pin: PE11\nenable_pin: !PF2\nrotation_distance: 40\nmicrosteps: 16\nfull_steps_per_rotation: 200  \nendstop_pin: ^PG12\nposition_endstop: -0\nposition_min: -0\nposition_max: 310\nhoming_speed: 35  \nhoming_retract_dist: 0\n[tmc5160 stepper_y] \nspi_bus: spi1a\n#spi_software_mosi_pin: PB5\n#spi_software_miso_pin: PB4\n#spi_software_sclk_pin: PB3\ncs_pin: PE10\ninterpolate: false\nrun_current: 1.75\nsense_resistor: 0.033\ndriver_TBL: 0\ndriver_TOFF: 1\n\n\n\n\n[stepper_y1]\n##	in M6 position\nstep_pin: PE0\ndir_pin: PG3\nenable_pin: !PG4\nrotation_distance: 40\nmicrosteps: 16\nfull_steps_per_rotation: 200 \n[tmc5160 stepper_y1] \nspi_bus: spi1a\n#spi_software_mosi_pin: PB5\n#spi_software_miso_pin: PB4\n#spi_software_sclk_pin: PB3\ncs_pin: PG2\ninterpolate: false\nrun_current: 1.75\nsense_resistor: 0.033\ndriver_TBL: 0\ndriver_TOFF: 1\n\n#########################\n\n[stepper_z]\n## In M0 position\nstep_pin: PE2\ndir_pin: !PC5\nenable_pin: !PF11\nmicrosteps: 32\nrotation_distance: 4\nendstop_pin: ^PG10\nposition_endstop: 0.0\nposition_max: 400\nfull_steps_per_rotation: 200\nhoming_retract_dist: 5.0\nhoming_positive_dir: false\nhoming_speed: 5.0\nsecond_homing_speed: 2.5\n[tmc2209 stepper_z]\ninterpolate: false\nuart_pin: PC4\nrun_current: 1\nsense_resistor: 0.110\n\n\n#########################\n\n[extruder]\n## In M1 position\n### Vz-HextrudORT\nstep_pin: PE3\ndir_pin: PF13\nmicrosteps: 16\nenable_pin: !PF14\nrotation_distance: 22 \ngear_ratio: 50:10 \nnozzle_diameter: 0.5\nfilament_diameter: 1.750\nheater_pin: PB0\nsensor_type: PT1000\nsensor_pin: PF4\n###\ncontrol: pid\npid_Kp=28.737 \npid_Ki=1.935 \npid_Kd=106.684\nmin_temp: 0\nmax_temp: 400\nfull_steps_per_rotation: 200\nmax_extrude_only_distance: 1000.0\nmax_extrude_cross_section: 500\nmax_extrude_only_velocity: 200\nmax_extrude_only_accel: 10000\nmin_extrude_temp: 0\npressure_advance: 0.02\npressure_advance_smooth_time: 0.03\n[tmc2209 extruder]\ninterpolate: false\nuart_pin: PF12\nrun_current: 0.7\nsense_resistor: 0.110\n\n\n\n########################\n########################\n\n[heater_bed]\nheater_pin: PE5\nsensor_type: Generic 3950\nsensor_pin: PC1\ncontrol: pid\npid_Kp: 66.746\npid_Ki: 3.504\npid_Kd: 317.878\nmin_temp: 0\nmax_temp: 130\n\n\n########################\n#### FANS ##############\n########################\n\n[fan]\n##	Print Cooling Fan \npin: PF8\nmax_power: 0.9\ncycle_time: 0.002\nhardware_pwm: false\nshutdown_speed: 0\n\n[heater_fan hotend_fan]\n##	Hotend Fan - FAN0 Connector (or watercooling pump+fan on radiator)\npin: PA0\nmax_power: 1.0\nkick_start_time: 0.5\nheater: extruder\nheater_temp: 50.0\n\n[fan_generic Exhaust_fan]\n##  exhaust fan - In FAN4\npin: PA15\nmax_power: 1\nshutdown_speed: 0\nkick_start_time: 0.1\noff_below: 0.10\n\n[fan_generic chamber_fan]\n##  chamber fan - In FAN9 Positon\npin: PD15\nmax_power: 1\nshutdown_speed: 0\nkick_start_time: 0.1\noff_below: 0.10\n\n[fan_generic RSCS]\n##  RSCS Fans - In FAN1 Positon\npin: PA1\nmax_power: 1\nshutdown_speed: 0\nkick_start_time: 0.1\noff_below: 0.10\n\n#[controller_fan driver_fan] \n# This is if you connect your driver fan to Super8 \n#or if you want to add fan to the Super8. Other wise connect them in the driver fan ports\n#stepper: stepper_x\n#pin: PB11\n#max_power: 1\n#shutdown_speed: 0\n#kick_start_time: 0.1\n#off_below: 0.10\n\n########################\n########################\n[temperature_sensor FLY-Super8]\nsensor_type: temperature_mcu\n\n#[temperature_sensor chamber]  optional for chamber temp\n#sensor_type: NTC 100K MGB18-104F39050L32\n#pullup_resistor: 4700\n#sensor_pin: PF9\n#min_temp: 0\n#max_temp: 60\n#gcode_id: C: C\n\n\n[temperature_sensor raspberry_pi]\nsensor_type: temperature_host\n\n\n########################\n########################\n\n[pause_resume]\nrecover_velocity: 350\n\n########################\n########################\n\n[filament_switch_sensor sentinel]\npause_on_runout: true\nswitch_pin: PA8\nrunout_gcode:\n    G91\n    G1 E-30 F2500\n    G90\n    G1 X0 Y0 F30000\n    M104 S0\n\n########################\n########################\n\n#[homing_override]\n#set_position_z: 0\n#axes: xy\n#gcode:\n# G0 Z3 F200\n# G28 y0 \n# G28 x0\n# G28 Z0\n\n########################\n########################\n\n[firmware_retraction]\nretract_length: 0.5\nretract_speed: 60\nunretract_extra_length: 0.00\nunretract_speed: 60\n\n###########################################\n#########Custom PINs Definition ############\n###########################################\n\n\n[output_pin LED]\n#In FAN8 position\npin: PD14\npwm: false\nvalue: 1\nshutdown_value:1\n### gcode command: SET_PIN PIN=LED value=0 to 1\n\n\n\n################################################################################################\n################################################################################################\n#####				MACROS					\n\n##################\n[gcode_macro RETRACTION_UP]\ngcode:\n    {% set CRLEN = printer.firmware_retraction.retract_length|float %}\n    {% set NRLEN = CRLEN|float + 0.1 %}\n    { action_respond_info("current retract_length %.2f, new retract_length %.2f" % (CRLEN, NRLEN))  }\n    SET_RETRACTION RETRACT_LENGTH={NRLEN}    \n\n[gcode_macro RETRACTION_DOWN]\ngcode:\n    {% set CRLEN = printer.firmware_retraction.retract_length|float %}\n    {% set NRLEN = CRLEN|float - 0.1 %}\n    { action_respond_info("current retract_length %.2f, new retract_length %.2f" % (CRLEN, NRLEN))  }\n    SET_RETRACTION RETRACT_LENGTH={NRLEN}   \n\n\n#[gcode_macro SET_RETRACTIONLENGTH]\n#gcode:\n#  SET_RETRACTION RETRACT_LENGTH={params.LENGTH|float}\n#  GET_RETRACTION\n\n########################	\n[gcode_macro exhaustfan_on]\ngcode: \n    SET_FAN_SPEED FAN=Exhaust_fan SPEED=1\n###	\n    \n[gcode_macro exhaustfan_off]\ngcode: \n    SET_FAN_SPEED FAN=Exhaust_fan SPEED=0\n    \n    \n###		\n    \n[gcode_macro enclosurefan_on]\ngcode: \n    SET_FAN_SPEED FAN=chamber_fan SPEED=1\n\n###			\n    \n[gcode_macro enclosurefan_off]\ngcode: \n    SET_FAN_SPEED FAN=chamber_fan SPEED=0\n\n\n[gcode_macro RSCS_on]\ngcode: \n    SET_FAN_SPEED FAN=RSCS SPEED=1\n###	\n    \n\n[gcode_macro RSCS_off]\ngcode: \n    SET_FAN_SPEED FAN=RSCS SPEED=0\n        \n###\n\n[gcode_macro LED_on]\ngcode:\n    SET_PIN PIN=LED value=1   \n\n\n[gcode_macro LED_off]\ngcode:\n    SET_PIN PIN=LED value=0  \n\n###	\n    \n[gcode_macro PA_tunning]\ngcode: \n    SET_VELOCITY_LIMIT SQUARE_CORNER_VELOCITY=1 ACCEL=500\n    TUNING_TOWER COMMAND=SET_PRESSURE_ADVANCE PARAMETER=ADVANCE START=0 FACTOR=.005\n\n\n#####\n\n[gcode_macro POWER_OFF]\ngcode:\n{action_call_remote_method("set_device_power",\n                            device="tplink",\n                            state="off")}\n#####	\n\n[gcode_macro TEST_RESONNANCES_X]\ngcode:\nTEST_RESONANCES AXIS=X\n\n\n[gcode_macro TEST_RESONNANCES_Y]\ngcode:\nTEST_RESONANCES AXIS=Y\n\n[gcode_macro DUMP_WARNINGS]\ndescription: Debug: Print all warning messages from klipper\ngcode:\n{% set parameters = ["printer.configfile.warnings:"] %}\n{% for warning in printer.configfile.warnings %}\n    {% set parameters = parameters.append("%s -> %s -> %s\\n%s" % (warning.type, warning.section, warning.option, warning.message)) %}\n{% endfor %}\n{action_respond_info(parameters|join("\\n"))}\n\n\n[gcode_macro ACCELL_TEST_X]\ngcode:\n\n    {% set steps = params.STEPS|default(100)|int %}\n    {% set speed = params.VELOCITY|default(1000)|float * 60 %}\n\n    {% set inset = 10.0|float %}\n    {% set accel = 10000|int %}\n    {% set maxX = printer.configfile.settings.stepper_x.position_max|float - inset %}\n    {% set maxY = printer.configfile.settings.stepper_y.position_max|float - inset %}\n    {% set minX = printer.configfile.settings.stepper_x.position_min|float + inset %}\n    {% set minY = printer.configfile.settings.stepper_y.position_min|float + inset %}\n\n    SAVE_GCODE_STATE NAME=accelltest_state\n\n    SET_VELOCITY_LIMIT ACCEL={accel} \n    SET_VELOCITY_LIMIT ACCEL_TO_DECEL={accel}\n    G28\n    G1 Z5\n    G1 X{minX} Y{minY} F{speed} \n\n    {% for INTERVAL in range(steps) %}\n        {% set eff = accel + (INTERVAL * 1000) %} \n        SET_VELOCITY_LIMIT ACCEL={eff} \n        SET_VELOCITY_LIMIT ACCEL_TO_DECEL={eff}\n        G1 X{minX} Y{minY} F{speed}  \n        G1 X{maxX} Y{maxY} F{speed}  \n\n    {% endfor %}    \n\n    RESTORE_GCODE_STATE NAME=accelltest_state \n    \n    \n    \n[gcode_macro ACCELL_TEST_Y]\ngcode:\n\n    {% set steps = params.STEPS|default(100)|int %}\n    {% set speed = params.VELOCITY|default(1000)|float * 60 %}\n\n    {% set inset = 10.0|float %}\n    {% set accel = 10000|int %}\n    {% set maxX = printer.configfile.settings.stepper_x.position_max|float - inset %}\n    {% set maxY = printer.configfile.settings.stepper_y.position_max|float - inset %}\n    {% set minX = printer.configfile.settings.stepper_x.position_min|float + inset %}\n    {% set minY = printer.configfile.settings.stepper_y.position_min|float + inset %}\n\n    SAVE_GCODE_STATE NAME=accelltest_state\n\n    SET_VELOCITY_LIMIT ACCEL={accel} \n    SET_VELOCITY_LIMIT ACCEL_TO_DECEL={accel}\n    G28\n    G1 Z5\n    G1 X{minX} Y{minY} F{speed} \n\n    {% for INTERVAL in range(steps) %}\n        {% set eff = accel + (INTERVAL * 1000) %} \n        SET_VELOCITY_LIMIT ACCEL={eff} \n        SET_VELOCITY_LIMIT ACCEL_TO_DECEL={eff}\n        G1 X{maxX} Y{minY} F{speed}  \n        G1 X{minX} Y{maxY} F{speed}  \n\n    {% endfor %}    \n\n    RESTORE_GCODE_STATE NAME=accelltest_state \n\n\n[gcode_macro enable_stepper]\ngcode:\nSET_STEPPER_ENABLE STEPPER=stepper_x ENABLE=1\nSET_STEPPER_ENABLE STEPPER=stepper_x1 ENABLE=1\nSET_STEPPER_ENABLE STEPPER=stepper_y ENABLE=1\nSET_STEPPER_ENABLE STEPPER=stepper_y1 ENABLE=1\n\n################################################################################################\n################################################################################################\n\n\n\n[virtual_sdcard]\npath: ~/gcode_files\n\n########################\n########################\n\n[display_status]\n\n########################\n########################\n\n[bed_screws]\nscrew1: 20,20\nscrew2: 20,290\nscrew3: 290,290\nscrew4: 290,20\n\n\n########################\n########################\n\n[input_shaper]\nshaper_freq_x: 118\nshaper_type_x: mzv\nshaper_freq_y: 81\nshaper_type_y: mzv\n\n'})})]})}function d(n={}){let{wrapper:e}={...(0,i.a)(),...n.components};return e?(0,o.jsx)(e,{...n,children:(0,o.jsx)(c,{...n})}):c(n)}},50065:function(n,e,t){t.d(e,{Z:function(){return a},a:function(){return s}});var r=t(67294);let o={},i=r.createContext(o);function s(n){let e=r.useContext(i);return r.useMemo(function(){return"function"==typeof n?n(e):{...e,...n}},[e,n])}function a(n){let e;return e=n.disableParentContext?"function"==typeof n.components?n.components(o):n.components||o:s(n.components),r.createElement(i.Provider,{value:e},n.children)}}}]);