"use strict";(self.webpackChunkklipper_docs=self.webpackChunkklipper_docs||[]).push([["59626"],{42190:function(n,e,t){t.r(e),t.d(e,{frontMatter:()=>s,default:()=>l,contentTitle:()=>a,assets:()=>p,toc:()=>_,metadata:()=>r});var r=JSON.parse('{"id":"ProductDoc/MainBoard/fly-d/fly-d8-h723/cfg","title":"Klipper Reference Configuration","description":"","source":"@site/i18n/en/docusaurus-plugin-content-docs/current/ProductDoc/MainBoard/fly-d/fly-d8-h723/cfg.mdx","sourceDirName":"ProductDoc/MainBoard/fly-d/fly-d8-h723","slug":"/ProductDoc/MainBoard/fly-d/fly-d8-h723/cfg","permalink":"/fly-docs-next/en/docs/ProductDoc/MainBoard/fly-d/fly-d8-h723/cfg","draft":false,"unlisted":false,"editUrl":"https://github.com/kluoyun/fly-docs-next/tree/master/docs/ProductDoc/MainBoard/fly-d/fly-d8-h723/cfg.mdx","tags":[],"version":"current","lastUpdatedBy":"xishangyue","lastUpdatedAt":1746004502000,"sidebarPosition":5,"frontMatter":{"sidebar_position":5,"sidebar_label":"Klipper Configuration Reference"},"sidebar":"tutorialSidebar","previous":{"title":"Wiring Reference","permalink":"/fly-docs-next/en/docs/ProductDoc/MainBoard/fly-d/fly-d8-h723/wiring"},"next":{"title":"CAN Bridge","permalink":"/fly-docs-next/en/docs/ProductDoc/MainBoard/fly-d/fly-d8-h723/canbridge"}}'),o=t(85893),i=t(50065);let s={sidebar_position:5,sidebar_label:"Klipper Configuration Reference"},a="Klipper Reference Configuration",p={},_=[];function d(n){let e={code:"code",h1:"h1",header:"header",pre:"pre",...(0,i.a)(),...n.components};return(0,o.jsxs)(o.Fragment,{children:[(0,o.jsx)(e.header,{children:(0,o.jsx)(e.h1,{id:"klipper-reference-configuration",children:"Klipper Reference Configuration"})}),"\n",(0,o.jsx)(e.pre,{children:(0,o.jsx)(e.code,{className:"language-cfg",children:"####################################################################################\n#                         3D MELLOW /FLY-D8 PRO                                   #\n####################################################################################\n## FLY-D8 PRO Documentation: https://mellow-next.klipper.cn/docs/category/fly-d8-h723\n## FLY Official Taobao Store: https://shop126791347.taobao.com/shop/view_shop.htm?spm=a230r.1.14.4.1a4840a8hyvpPJ&user_number_id=2464680006\n## For after-sales support, please contact Taobao customer service\n## FLY After-sales Technical Support Group: 621032883\n## FLY-RRF Firmware Group: 786561979\n\n#####################################################################\n#                              Notes                                #\n#####################################################################\n##***Items to Change/Check:***\n## MCU Path                               [mcu]\n## Printer movement range                  xyz position\n## Thermistor type                         [extruder] and [heater_bed]\n## Z endstop stop position                 [homing_override]\n## Z endstop offset position               [stepper_z]\n## PID tuning                              [extruder] and [heater_bed]\n## Extruder motor step tuning              [extruder]\n\n#####################################################################\n#                            File Includes                          #\n#####################################################################\n#[include fluidd.cfg]          # For FLUIDD.\n#[include mainsail.cfg]        # For MAINSAIL.\n#Choose the file you need to use.\n\n#####################################################################\n#                            Mainboard Config                       #\n#####################################################################\n[mcu]                           # FLY board ID\nserial: /dev/serial/by-id/usb-Klipper_stm32f407xx_XXXXXXXXXXXXXXXXXXXXX\n### Find USB firmware ID: ls -l /dev/serial/by-id/\n### Replace /dev/serial/by-id/usb-Klipper_stm32f407xx_XXXXXXXXXXXXXXXXXXXXX with the ID you found.\n\n#canbus_uuid: e51d5c71a901\n### Find CAN firmware ID: ~/klippy-env/bin/python ~/klipper/scripts/canbus_query.py can0\n### For CAN ID, replace 'serial' with 'canbus_uuid:' and append the ID.\n\n#####################################################################\n#                        Printer and Acceleration                   #\n#####################################################################\n[printer]                       # Printer settings\nkinematics: corexy              # Kinematics: cartesian or corexy, etc.\nmax_velocity: 300               # Max printer speed\nmax_accel: 3000                 # Max acceleration (max 3000)\nmax_z_velocity: 15              # Max Z speed\nmax_z_accel: 100                # Max Z acceleration\nsquare_corner_velocity: 5.0     # Square corner velocity, lower values help reduce inertia\n\n#####################################################################\n#                          Temperature Monitoring                   #\n#####################################################################\n[temperature_sensor Fly-D8]     # FLY board temperature (rename as needed)\nsensor_type: temperature_mcu    # Linked to MCU (default)\n#--------------------------------------------------------------------\n[temperature_sensor FLY-\u03C0]      # Host temperature\nsensor_type: temperature_host   # Linked to host\n\n#####################################################################\n#                        Bed Mesh Calibration\n#####################################################################\n[bed_mesh]\nspeed: 50                    # Calibration speed\nhorizontal_move_z: 5         # Z lift before moving to next point\nmesh_min: 30,30              # Minimum calibration point (x, y)\nmesh_max: 270, 270           # Maximum calibration point (x, y)\nprobe_count: 4,4             # Number of probe points (4x4 = 16 points)\nmesh_pps: 2,2                # Additional probe points\nalgorithm: bicubic           # Algorithm model\nbicubic_tension: 0.2\n\n#####################################################################\n#             X/Y Stepper Motor Settings                            #\n#####################################################################\n#                   z1                 z2\n#                       B__________A\n#\n#\n#\n#                   Z0     12864      Z3\n\n#####################################################################\n#                  X Stepper Motor      (DRIVER0)                   #\n#####################################################################\n## DRIVER0 motor position\n[stepper_x]\nstep_pin: PE5                       # X step pin\ndir_pin: PA8                        # X direction pin (add ! to reverse)\nenable_pin: !PA15                   # X enable pin (add !, otherwise motor won't work)\nrotation_distance: 40               # Pulley circumference in mm (2GT-20T: 40, 2GT-16T: 32)\nmicrosteps: 16                      # Microstepping (higher = better quality, more load)\nfull_steps_per_rotation: 200        # Steps per revolution (1.8\xb0: 200, 0.9\xb0: 400)\nendstop_pin: PD9                    # Endstop pin (normally closed recommended)\n#endstop_pin: tmc5160_stepper_x:virtual_endstop\n### Add ! to reverse state if needed for safety\nposition_min: 0                     # Min soft limit\nposition_endstop: 300               # Max soft limit (250mm-300mm-350mm)\nposition_max: 300                   # Max mechanical limit (250mm-300mm-350mm)\nhoming_speed: 50                    # Homing speed (max 100)\nhoming_retract_dist: 5              # Retract distance after first trigger\n#--------------------------------------------------------------------\n## Ensure correct driver model (2208 or 2209)\n[tmc2209 stepper_x]\nuart_pin: PC9                       # UART pin\ninterpolate: False                  # 256 microstep interpolation (True/False)\nrun_current: 0.8                    # Motor current (A)\nsense_resistor: 0.110               # Do not change\nstealthchop_threshold: 0            # StealthChop threshold (set to 0 to disable)\n#--------------------------------------------------------------------\n#[tmc5160 stepper_x]\n#cs_pin: PC9\n#spi_bus: spi1a\n#run_current: 1.0\n#interpolate: False\n#sense_resistor: 0.075\n#stealthchop_threshold: 0\n\n#####################################################################\n#                  Y Stepper Motor      (DRIVER1)                   #\n#####################################################################\n## DRIVER1 motor position\n[stepper_y]\nstep_pin: PE4\ndir_pin: PC11\nenable_pin: !PC12\nrotation_distance: 40\nmicrosteps: 16\nfull_steps_per_rotation: 200\nendstop_pin: !PD8                   # Endstop pin (normally closed, add ! to reverse)\n#endstop_pin: tmc5160_stepper_y:virtual_endstop\nposition_min: 0\nposition_endstop: 300\nposition_max: 300\nhoming_speed: 50\nhoming_retract_dist: 5\n#--------------------------------------------------------------------\n## Ensure correct driver model (2209 or 5160)\n[tmc2209 stepper_y]\nuart_pin: PC10\ninterpolate: False\nrun_current: 0.8\nsense_resistor: 0.110\nstealthchop_threshold: 0\n#--------------------------------------------------------------------\n#[tmc5160 stepper_y]\n#cs_pin: PC10\n#spi_bus: spi1a\n#run_current: 1.0\n#interpolate: False\n#sense_resistor: 0.075\n#stealthchop_threshold: 0\n\n#####################################################################\n#                     Z Stepper Motors                              #\n#####################################################################\n## DRIVER2 motor position\n[stepper_z]\nstep_pin: PE3\ndir_pin: !PD1\nenable_pin: !PD2\nrotation_distance: 40\ngear_ratio: 80:16\nmicrosteps: 32\nendstop_pin: !PD11\nposition_max: 290\nposition_endstop: -0.5\nposition_min: -5\nhoming_speed: 10\nsecond_homing_speed: 3\nhoming_retract_dist: 3\n#--------------------------------------------------------------------\n[tmc2209 stepper_z]\nuart_pin: PD0\ninterpolate: false\nrun_current: 0.8\nsense_resistor: 0.110\nstealthchop_threshold: 0\n##--------------------------------------------------------------------\n## DRIVER4 motor position\n[stepper_z1]\nstep_pin: PE2\ndir_pin:  PD4\nenable_pin: !PD5\nrotation_distance: 40\ngear_ratio: 80:16\nmicrosteps: 32\n[tmc2209 stepper_z1]\nuart_pin: PD3\ninterpolate: false\nrun_current: 1.0\nsense_resistor: 0.110\nstealthchop_threshold: 0\n#--------------------------------------------------------------------\n## DRIVER5 motor position\n[stepper_z2]\nstep_pin: PE1\ndir_pin: !PD7\nenable_pin: !PB6\nrotation_distance: 40\ngear_ratio: 80:16\nmicrosteps: 32\n[tmc2209 stepper_z2]\nuart_pin: PD6\ninterpolate: false\nrun_current: 1.0\nsense_resistor: 0.110\nstealthchop_threshold: 0\n##--------------------------------------------------------------------\n## DRIVER6 motor position\n[stepper_z3]\nstep_pin: PE0\ndir_pin: PC13\nenable_pin: !PC14\nrotation_distance: 40\ngear_ratio: 80:16\nmicrosteps: 32\n[tmc2209 stepper_z3]\nuart_pin: PB7\ninterpolate: False\nrun_current: 1.0\nsense_resistor: 0.110\nstealthchop_threshold: 0\n\n#####################################################################\n#                         Extruder Configuration\n#####################################################################\n## DRIVER7 motor position\n[extruder]\nstep_pin:PE8\ndir_pin:PE12\nenable_pin: !PE11\nmicrosteps: 16\nfull_steps_per_rotation: 200\nrotation_distance: 22.52245\n# Calibrate: rotation_distance = <old_rotation_distance> * <actual_extrude_length> / <requested_extrude_length>\ngear_ratio: 50:10\nnozzle_diameter: 0.400\nfilament_diameter: 1.750\nheater_pin: PD12\nsensor_type: Generic 3950\nsensor_pin: PC5\nmax_power: 1.0\nmin_temp: -235\nmax_temp: 350\nmin_extrude_temp: 150\npressure_advance: 0.04\npressure_advance_smooth_time:0.040\n# PID tune: \"PID_CALIBRATE HEATER=extruder TARGET=245\"\ncontrol = pid\npid_kp = 26.213\npid_ki = 1.304\npid_kd = 131.721\n\n[tmc2209 extruder]\nuart_pin:PC15\ninterpolate: False\nrun_current: 0.6\nsense_resistor: 0.110\nstealthchop_threshold: 0\n\n#####################################################################\n#                         Heated Bed Configuration\n#####################################################################\n[heater_bed]\nheater_pin: PB1\nsensor_type: Generic 3950\nsensor_pin: PB0\nmax_power: 1.0\nmin_temp: -235\nmax_temp: 120\n# PID tune: \"PID_CALIBRATE HEATER=heater_bed TARGET=100\"\ncontrol: pid\npid_kp: 58.437\npid_ki: 2.347\npid_kd: 363.769\n#####################################################################\n#                             Fan Configuration\n#####################################################################\n[fan]                        # Model cooling fan\npin:PA1\nkick_start_time: 1.0\noff_below: 0.10\nhardware_pwm: true\nmax_power: 1.0\n#--------------------------------------------------------------------\n[heater_fan Throat_Cooling]   # Throat cooling fan\npin:PA2\nmax_power: 1.0\nkick_start_time: 0.5\nheater: extruder\nheater_temp: 50\nfan_speed: 1.0\n#--------------------------------------------------------------------\n[heater_fan Bed_Fan]          # Electronics bay fan\npin: PA3\nmax_power: 1.0\nshutdown_speed: 0.0\nkick_start_time: 5.0\nheater: heater_bed\nheater_temp: 50\nfan_speed: 1.0\n#---------------------------------------------------------------\n[fan_generic Bed_Fan1]\npin:PC6\nmax_power: 1.0\n# #--------------------------------------------------------------------\n[fan_generic Side_Fan]\npin:PC7\nmax_power: 1.0\n# #--------------------------------------------------------------------\n[fan_generic Side_Fan1]\npin:PC8\nmax_power: 1.0\n\n#####################################################################\n#                       Idle Timeout for Heated Bed                 #\n#####################################################################\n[idle_timeout]\ntimeout: 1800                # Turn off heated bed after 30 minutes idle\n\n#####################################################################\n#                           Bed Leveling Probe                      #\n#####################################################################\n# Default PL08N probe should not be lower than nozzle, only for leveling.\n# If your PL08N is NO (normally open), add ! to the pin.\n[probe]\npin: ^PC0                   # Endstop pin (normally closed, add ! to reverse)\nx_offset: 0\ny_offset: 25.0\nz_offset: 0\nspeed: 10.0\nsamples: 3\nsamples_result: median\nsample_retract_dist: 4.0\nsamples_tolerance: 0.007\nsamples_tolerance_retries: 3\n#--------------------------------------------------------------------\n#[bltouch]\n#sensor_pin: ^PC0\n#control_pin: PE6\n#x_offset: 0\n#y_offset: 25.0\n#z_offset: 0\n\n#####################################################################\n#                               Homing                              #\n#####################################################################\n[safe_z_home]\nhome_xy_position:206,300\nspeed:100\nz_hop:10\n#--------------------------------------------------------------------\n#[homing_override]\n#axes: z\n#set_position_z: 0\n#gcode:\n#   G90\n#   G0 Z15 F600\n#   G28 X Y\n#    ## Update X0 and Y0 to your values (e.g., X157, Y305)\n#    ## Z endstop position\n#   G0 X185 Y250 F3600    #250mm\n#   G28 Z\n#   G0 Z10 F1800\n\n#####################################################################\n#                           4Z Leveling\n#####################################################################\n[quad_gantry_level]          # 300mm machine leveling parameters\n#gantry_corners:\n#    -60,-10\n#    310,320\n#points:                      # 250mm machine leveling points\n#    50,25\n#    50,175\n#    200,175\n#    200,25\n#--------------------------------------------------------------------\ngantry_corners:              # 300mm machine leveling points\n    -60,-10\n    360,370\npoints:\n    50,25\n    50,225\n    250,225\n    250,25\n#--------------------------------------------------------------------\n#gantry_corners:              # 350mm machine leveling points\n#    -60,-10\n#    410,420\n#points:\n#    50,25\n#    50,275\n#    300,275\n#    300,25\n#--------------------------------------------------------------------\nspeed: 100\nhorizontal_move_z: 10\nretry_tolerance: 0.0075\nretries: 5\nmax_adjust: 10\n\n#####################################################################\n#                            LED Configuration (Enable if needed)\n#####################################################################\n[neopixel sb_leds]\npin: PD15\nchain_count: 3\ncolor_order: GRBW\ninitial_RED: 0.4\ninitial_GREEN: 0.8\ninitial_BLUE: 1\ninitial_WHITE: 0.0\n#--------------------------------------------------------------------\n[board_pins]\naliases:\n    EXP1_1=PB11, EXP1_3=PA14,  EXP1_5=PB10, EXP1_7=PE14, EXP1_9=<GND>,\n    EXP1_2=PE7,  EXP1_4=PA13,  EXP1_6=PE15, EXP1_8=PA0 , EXP1_10=<5V>,\n    # EXP2 header\n    EXP2_1=PB14, EXP2_3=PA9,  EXP2_5=PA10,  EXP2_7=PE13,  EXP2_9=<GND>,\n    EXP2_2=PB13, EXP2_4=PB12, EXP2_6=PB15,  EXP2_8=<RST>, EXP2_10=<NC>,\n#--------------------------------------------------------------------\n[display]\nlcd_type: uc1701\ncs_pin: EXP1_3\na0_pin: EXP1_4\nrst_pin: EXP1_5\ncontrast: 63\nencoder_pins: ^EXP2_5, ^!EXP2_3\nclick_pin: ^!EXP1_2\n# --------------------------------------------------------------------\n#### For FLY Mini12864\n[neopixel fly_mini12864]\npin: EXP1_6\nchain_count: 3\ninitial_RED: 0.5\ninitial_GREEN: 0.5\ninitial_BLUE: 0.5\ncolor_order: RGB\n#####################################################################\n#                           Custom gcode Macros\n#####################################################################\n[gcode_macro PRINT_START]\ngcode:\n    G92 E0                           # Reset extruder\n    BED_MESH_CLEAR                   # Unload bed mesh\n    G28                              # Home all axes\n    clean_nozzle                     # Clean nozzle\n    QUAD_GANTRY_LEVEL                # Gantry leveling\n    G28                              # Home all axes\n    G1 Z20 F3000                     # Move nozzle away from bed\n    clean_nozzle\n    # BED_MESH_CALIBRATE PRINT_MIN={params.PRINT_MIN} PRINT_MAX={params.PRINT_MAX} FORCE_NEW_MESH=True\n    BED_MESH_PROFILE LOAD=default    # Load bed mesh\n#--------------------------------------------------------------------\n[gcode_macro PRINT_huaxian]\ngcode:\n    G1 Z5 F3000\n    G92 E0                           # Reset extruder\n    G90                              # Absolute positioning\n    G0 X5 Y1 F6000                   # Move to x5 y1 at 100mm/s\n    G0 Z0.4\n    G91                              # Relative positioning\n    G1 X100 E20 F1200                # Move x 100mm and extrude\n    G1 Y1\n    G1 X-100 E20 F1200               # Move x -100mm and extrude\n    G0 z5\n    G92 E0                           # Reset extruder\n    G90                              # Absolute positioning\n#--------------------------------------------------------------------\n[gcode_macro PRINT_END]\ngcode:\n    M400                             # Wait for buffer to clear\n    G92 E0                           # Reset extruder\n    G1 E-10.0 F3600                  # Retract filament\n    G91                              # Relative positioning\n    G0 Z1.00 X20.0 Y20.0 F6000       # Move nozzle to remove stringing\n    TURN_OFF_HEATERS                 # Turn off heaters\n    M107                             # Turn off fan\n    G1 Z2 F3000                      # Move nozzle up 2mm\n    G90                              # Absolute positioning\n    G0  X150 Y280 F3600              # Move nozzle to rear\n    BED_MESH_CLEAR                   # Unload bed mesh\n\n"})})]})}function l(n={}){let{wrapper:e}={...(0,i.a)(),...n.components};return e?(0,o.jsx)(e,{...n,children:(0,o.jsx)(d,{...n})}):d(n)}},50065:function(n,e,t){t.d(e,{Z:()=>a,a:()=>s});var r=t(67294);let o={},i=r.createContext(o);function s(n){let e=r.useContext(i);return r.useMemo(function(){return"function"==typeof n?n(e):{...e,...n}},[e,n])}function a(n){let e;return e=n.disableParentContext?"function"==typeof n.components?n.components(o):n.components||o:s(n.components),r.createElement(i.Provider,{value:e},n.children)}}}]);