"use strict";(self.webpackChunkklipper_docs=self.webpackChunkklipper_docs||[]).push([["42161"],{52973:function(n,e,t){t.r(e),t.d(e,{frontMatter:()=>s,default:()=>d,contentTitle:()=>_,assets:()=>p,toc:()=>a,metadata:()=>r});var r=JSON.parse('{"id":"ProductDoc/MainBoard/fly-micro/fly-micro4/cfg","title":"Klipper\u53C2\u8003\u8A2D\u5B9A","description":"","source":"@site/i18n/ja/docusaurus-plugin-content-docs/current/ProductDoc/MainBoard/fly-micro/fly-micro4/cfg.mdx","sourceDirName":"ProductDoc/MainBoard/fly-micro/fly-micro4","slug":"/ProductDoc/MainBoard/fly-micro/fly-micro4/cfg","permalink":"/fly-docs-next/ja/docs/ProductDoc/MainBoard/fly-micro/fly-micro4/cfg","draft":false,"unlisted":false,"editUrl":"https://github.com/kluoyun/fly-docs-next/tree/master/docs/ProductDoc/MainBoard/fly-micro/fly-micro4/cfg.mdx","tags":[],"version":"current","lastUpdatedBy":"xishangyue","lastUpdatedAt":1746528673000,"sidebarPosition":5,"frontMatter":{"sidebar_position":5,"sidebar_label":"Klipper\u53C2\u8003\u8A2D\u5B9A"},"sidebar":"tutorialSidebar","previous":{"title":"\u30E1\u30A4\u30F3\u30DC\u30FC\u30C9\u914D\u7DDA","permalink":"/fly-docs-next/ja/docs/ProductDoc/MainBoard/fly-micro/fly-micro4/wiring"},"next":{"title":"CAN\u30D6\u30EA\u30C3\u30B8","permalink":"/fly-docs-next/ja/docs/ProductDoc/MainBoard/fly-micro/fly-micro4/canbridge"}}'),i=t(85893),o=t(50065);let s={sidebar_position:5,sidebar_label:"Klipper\u53C2\u8003\u8A2D\u5B9A"},_="Klipper\u53C2\u8003\u8A2D\u5B9A",p={},a=[];function c(n){let e={code:"code",h1:"h1",header:"header",pre:"pre",...(0,o.a)(),...n.components};return(0,i.jsxs)(i.Fragment,{children:[(0,i.jsx)(e.header,{children:(0,i.jsx)(e.h1,{id:"klipper\u53C2\u8003\u8A2D\u5B9A",children:"Klipper\u53C2\u8003\u8A2D\u5B9A"})}),"\n",(0,i.jsx)(e.pre,{children:(0,i.jsx)(e.code,{className:"language-cfg",children:"####################################################################################\n#                                         3D MELLOW                                #\n####################################################################################\n## Fly micro\u8CC7\u6599\u30B5\u30A4\u30C8\uFF1Ahttp://mellow.klipper.cn/#/board/fly_micro/README\n## Fly micro\u56DE\u8DEF\u56F3\u30B5\u30A4\u30C8\uFF1Ahttp://mellow.klipper.cn/#/board/fly_micro/schematic\n## FLY \u516C\u5F0F\u6DD8\u5B9D\u5E97\uFF1Ahttps://shop126791347.taobao.com/shop/view_shop.htm?spm=a230r.1.14.4.1a4840a8hyvpPJ&user_number_id=2464680006\n## \u30A2\u30D5\u30BF\u30FC\u30B5\u30FC\u30D3\u30B9\u304C\u5FC5\u8981\u306A\u5834\u5408\u306F\u6DD8\u5B9D\u30AB\u30B9\u30BF\u30DE\u30FC\u30B5\u30FC\u30D3\u30B9\u306B\u3054\u9023\u7D61\u304F\u3060\u3055\u3044\n## FLY \u30A2\u30D5\u30BF\u30FC\u30B5\u30FC\u30D3\u30B9\u6280\u8853\u30B5\u30DD\u30FC\u30C8\u30B0\u30EB\u30FC\u30D7\uFF1A621032883\n## FLY#RRF\u30D5\u30A1\u30FC\u30E0\u30A6\u30A7\u30A2\u4EA4\u6D41\u30B0\u30EB\u30FC\u30D7\uFF1A786561979\n\n#####################################################################\n#      \u6CE8\u610F\u4E8B\u9805\n#####################################################################\n##***\u5909\u66F4/\u78BA\u8A8D\u304C\u5FC5\u8981\u306A\u9805\u76EE\uFF1A*** \n## MCU \u30D1\u30B9                                [mcu] \n## \u30D7\u30EA\u30F3\u30BF\u306E\u52D5\u4F5C\u7BC4\u56F2                       xyz\u306Eposition   \n## \u30B5\u30FC\u30DF\u30B9\u30BF\u306E\u7A2E\u985E                         [extruder] \u3068 [heater_bed]\n## Z\u8EF8\u30EA\u30DF\u30C3\u30C8\u30B9\u30A4\u30C3\u30C1\u505C\u6B62\u4F4D\u7F6E              [safe_z_home] \n## Z\u8EF8\u30EA\u30DF\u30C3\u30C8\u30B9\u30A4\u30C3\u30C1\u30AA\u30D5\u30BB\u30C3\u30C8\u4F4D\u7F6E         [stepper_z] \n## PID \u30AD\u30E3\u30EA\u30D6\u30EC\u30FC\u30B7\u30E7\u30F3                   [extruder] \u3068 [heater_bed] \n## \u30A8\u30AF\u30B9\u30C8\u30EB\u30FC\u30C0\u30FC\u30E2\u30FC\u30BF\u30FC\u306E\u30B9\u30C6\u30C3\u30D7\u5024\u5FAE\u8ABF\u6574 [extruder] \n\n#####################################################################\n#      \u30D5\u30A1\u30A4\u30EB\u547C\u3073\u51FA\u3057\n#####################################################################\n[include fluidd.cfg]          # FLUIDD\u547C\u3073\u51FA\u3057\u30D5\u30A1\u30A4\u30EB\u3002\n#[include mainsail.cfg]        # MAINSDIL\u547C\u3073\u51FA\u3057\u30D5\u30A1\u30A4\u30EB\u3002\n#\u3069\u3061\u3089\u306E\u30D5\u30A1\u30A4\u30EB\u3092\u4F7F\u3046\u304B\u306F\u3054\u81EA\u8EAB\u3067\u3054\u78BA\u8A8D\u304F\u3060\u3055\u3044\n\n#####################################################################\n#      Master ID \u8A2D\u5B9A\n#####################################################################\n[mcu]\nserial: /dev/serial/by-id/usb-Klipper_rp2040_MELLOW-if00\n### USB\u30D5\u30A1\u30FC\u30E0\u30A6\u30A7\u30A2ID\u306E\u78BA\u8A8D\u65B9\u6CD5\uFF1Als /dev/serial/by-id/*\n### /dev/serial/by-id/usb-Klipper_rp2040_MELLOW-if00\u3092\u78BA\u8A8D\u3057\u305FID\u306B\u7F6E\u304D\u63DB\u3048\u3066\u304F\u3060\u3055\u3044\n#canbus_uuid: 114514114514\n### CAN\u30D5\u30A1\u30FC\u30E0\u30A6\u30A7\u30A2ID\u306E\u78BA\u8A8D\u65B9\u6CD5\uFF1A~/klippy#env/bin/python ~/klipper/scripts/canbus_query.py can0\n### CAN\u306EID\u306Fserial\u3092canbus_uuid: \u306B\u7F6E\u304D\u63DB\u3048\u3066ID\u3092\u8FFD\u52A0\n\n#####################################################################\n#      \u6E29\u5EA6\u76E3\u8996\n#####################################################################\n[temperature_sensor Micro4]        #\u30E1\u30A4\u30F3\u30DC\u30FC\u30C9\u306E\u6E29\u5EA6\u8868\u793A\nsensor_type: temperature_mcu\n#####################################################################\n# [temperature_sensor lite2]       #\u30DB\u30B9\u30C8PC\u6E29\u5EA6\n# sensor_type: temperature_host\n\n#####################################################################\n#      \u30E2\u30C7\u30EB\u3068\u52A0\u901F\u5EA6\n#####################################################################\n[printer]\nkinematics: corexy\nmax_velocity: 200\nmax_accel: 2000\nmax_z_velocity: 15\nmax_z_accel: 300\nsquare_corner_velocity: 6.0\n\n#####################################################################\n#      X/Y \u30B9\u30C6\u30C3\u30D1\u30FC\u8A2D\u5B9A\n#####################################################################\n\n[stepper_x]\n## \u53C2\u7167\uFF1Ahttps://docs.vorondesign.com/build/startup/#v0\nstep_pin: gpio6\ndir_pin:gpio3                                    # \u30E2\u30FC\u30BF\u30FC\u306E\u56DE\u8EE2\u65B9\u5411\u306F\u4E0A\u8A18\u30EA\u30F3\u30AF\u3067\u78BA\u8A8D\u3002\u9006\u306E\u5834\u5408\u306Fgpio3\u306E\u524D\u306B!\u3092\u8FFD\u52A0\nenable_pin:!gpio7\nrotation_distance: 40\nmicrosteps: 32\nfull_steps_per_rotation: 200                    # 0.9\xb0\u30B9\u30C6\u30C3\u30D1\u30FC\u30E2\u30FC\u30BF\u30FC\u306F400\u30011.8\xb0\u306F200\nendstop_pin: tmc2209_stepper_x:virtual_endstop\nposition_endstop: 120\nposition_max: 120\nhoming_speed: 40                                # \u30BB\u30F3\u30B5\u30FC\u30EC\u30B9\u30DB\u30FC\u30DF\u30F3\u30B0\u6642\u306F40mm/s\u3092\u8D85\u3048\u306A\u3044\u3053\u3068\u3092\u63A8\u5968\nhoming_retract_dist: 0\n\n[tmc2209 stepper_x]\nuart_pin:gpio9\nuart_address:0\ninterpolate: False\nrun_current: 0.8\nsense_resistor: 0.110\nstealthchop_threshold: 0                        # 999999\u3067stealthchop\u30AA\u30F3\u30010\u3067spreadcycle\ndiag_pin: ^gpio13\ndriver_SGTHRS: 80\n\n[stepper_y]\n## \u53C2\u7167\uFF1Ahttps://docs.vorondesign.com/build/startup/#v0\nstep_pin:gpio1\ndir_pin:gpio0                                    # \u30E2\u30FC\u30BF\u30FC\u306E\u56DE\u8EE2\u65B9\u5411\u306F\u4E0A\u8A18\u30EA\u30F3\u30AF\u3067\u78BA\u8A8D\u3002\u9006\u306E\u5834\u5408\u306Fgpio0\u306E\u524D\u306B!\u3092\u8FFD\u52A0\nenable_pin:!gpio2\nrotation_distance: 40\nmicrosteps: 32\nfull_steps_per_rotation: 200\nendstop_pin: tmc2209_stepper_y:virtual_endstop\nposition_endstop: 120\nposition_max: 120\nhoming_speed: 40 \nhoming_retract_dist: 0\n\n[tmc2209 stepper_y]\nuart_pin:gpio9\nuart_address:1\ninterpolate: False\nrun_current: 0.8\nsense_resistor: 0.110\nstealthchop_threshold: 0\ndiag_pin: ^gpio14\ndriver_SGTHRS: 80\n\n#####################################################################\n#   Z \u30B9\u30C6\u30C3\u30D1\u30FC\u8A2D\u5B9A\n#####################################################################\n[stepper_z]\n## \u53C2\u7167\uFF1Ahttps://docs.vorondesign.com/build/startup/#v0\nstep_pin: gpio26\ndir_pin:!gpio25                                    # \u30E2\u30FC\u30BF\u30FC\u306E\u56DE\u8EE2\u65B9\u5411\u306F\u4E0A\u8A18\u30EA\u30F3\u30AF\u3067\u78BA\u8A8D\u3002\u9006\u306E\u5834\u5408\u306Fgpio25\u306E\u524D\u306B!\u3092\u8FFD\u52A0\nenable_pin: !gpio27\nrotation_distance: 8\nmicrosteps: 32\nendstop_pin: ^gpio12\nposition_endstop: 105\nposition_max: 105\nposition_min: -1.5\nhoming_speed: 20\nsecond_homing_speed: 3.0\nhoming_retract_dist: 3.0\n\n[tmc2209 stepper_z]\nuart_pin:gpio9\nuart_address:2\ninterpolate: False\nrun_current: 0.8\nsense_resistor: 0.110\nstealthchop_threshold: 0\n\n#####################################################################\n#   \u30A8\u30AF\u30B9\u30C8\u30EB\u30FC\u30C0\u30FC\n#####################################################################\n[extruder]\n## \u30BB\u30F3\u30B5\u30FC\u306E\u7A2E\u985E # \u3088\u304F\u4F7F\u308F\u308C\u308B\u30B5\u30FC\u30DF\u30B9\u30BF\u306F(Generic 3950, ATC Semitec 104GT#2)\n## \u30BB\u30F3\u30B5\u30FC\u30BF\u30A4\u30D7 # \u4E00\u822C\u7684\u306A\u30B5\u30FC\u30DF\u30B9\u30BF\u306F(Generic 3950, ATC Semitec 104GT#2)\nsensor_type: ATC Semitec 104GT-2\nsensor_pin: gpio28\n#####################################################################\n## PT1000\u3092\u4F7F\u7528\u3059\u308B\u5834\u5408\u306F\u3001\u30B5\u30FC\u30DF\u30B9\u30BF\u6A2A\u306E\u30B8\u30E3\u30F3\u30D1\u30FC\u3092\u63A5\u7D9A\u3057\u3066\u304F\u3060\u3055\u3044\u3002\n## PT1000\u3092\u4F7F\u3046\u5834\u5408\u306F\u30B5\u30FC\u30DF\u30B9\u30BF\u6A2A\u306E\u30B8\u30E3\u30F3\u30D1\u30FC\u3092\u63A5\u7D9A\n# sensor_type:PT1000\n#pullup_resistor: 1100\n#sensor_pin: gpio28\n\n#####################################################################\n#      \u30A8\u30AF\u30B9\u30C8\u30EB\u30FC\u30C0\u30FC\n#####################################################################\n## https://www.klipper3d.org/Config_Reference.html#extruder\n[extruder]\nstep_pin: gpio23\ndir_pin: gpio22\nenable_pin: !gpio24\nrotation_distance: 21.84\n## rotation_distance = \u5143\u306Erotation_distance \xd7 \u5B9F\u969B\u306E\u62BC\u51FA\u9577 \xf7 \u6307\u5B9A\u62BC\u51FA\u9577\n## \u30B9\u30C6\u30C3\u30D7\u5024\u306E\u30AD\u30E3\u30EA\u30D6\u30EC\u30FC\u30B7\u30E7\u30F3: 22.44=\u65E7\u502422*\u5B9F\u6E2C\u5024102/\u76EE\u6A19\u5024100\ngear_ratio:50:10\n## \u6E1B\u901F\u6BD4\uFF08Galileo\u30AE\u30A2\u6BD47.5:1\u3067\u3053\u306E\u884C\u306F\u30B3\u30E1\u30F3\u30C8\u30A2\u30A6\u30C8\uFF1BBMG\u306F50\uFF1A17\u3001\u51FA\u529B\u8EF8\u304C\u524D\u3001\u5165\u529B\u8EF8\u304C\u5F8C\u308D\uFF09\nmicrosteps:16\nfull_steps_per_rotation: 200    \nnozzle_diameter:0.400\nfilament_diameter:1.75\nheater_pin: gpio20\nmin_temp: -50\nmax_temp: 350\nmax_power: 1.0\nmin_extrude_temp: 150\npressure_advance: 0.05\n##Pressure in advance\n##\u5727\u529B\u30A2\u30C9\u30D0\u30F3\u30B9\n##https://www.klipper3d.org/ja/Pressure_Advance.html\npressure_advance_smooth_time: 0.040\n#max_extrude_only_distance: 200.0   # \u62BC\u51FA\u91CF\u30A8\u30E9\u30FC\u304C\u51FA\u308B\u5834\u5408\u306F\u30B3\u30E1\u30F3\u30C8\u30A2\u30A6\u30C8\u3001\u305F\u3060\u3057\u518D\u30B9\u30E9\u30A4\u30B9\u63A8\u5968\n#\u30CE\u30BA\u30EB\u6E29\u5EA6PID\u30AD\u30E3\u30EA\u30D6\u30EC\u30FC\u30B7\u30E7\u30F3\u30B3\u30DE\u30F3\u30C9\uFF1A  \"PID_CALIBRATE HEATER=extruder TARGET=245\ncontrol: pid\npid_kp: 26.213\npid_ki:1.304\npid_kd: 131.721\nstep_pulse_duration: 0.000004\n\n[tmc2209 extruder]\nuart_pin:gpio9\nuart_address:3\ninterpolate: False\nrun_current: 0.8\nsense_resistor: 0.110\nstealthchop_threshold: 0\n\n\n#####################################################################\n#   \u30D9\u30C3\u30C9\u30D2\u30FC\u30BF\u30FC\n#####################################################################\n[heater_bed]\nheater_pin: gpio21\n## \u30B5\u30FC\u30DF\u30B9\u30BF\u306E\u7A2E\u985E\u3092\u78BA\u8A8D\u3057\u3066\u304F\u3060\u3055\u3044\u3002https://www.klipper3d.org/Config_Reference.html#common#thermistors \u3092\u53C2\u7167\u3002\n## Keenovo\u30D2\u30FC\u30BF\u30FC\u306B\u306F\"Generic 3950\"\u3092\u4F7F\u7528\nsensor_type:Generic 3950\nsensor_pin: gpio29\nsmooth_time: 3.0\n#max_power: 0.6                                                     # 100w\u30D1\u30C3\u30C9\u306E\u307F\u5FC5\u8981\nmin_temp: -50\nmax_temp: 120\ncontrol: pid                                                        # \u521D\u671F\u30C1\u30A7\u30C3\u30AF\u5F8C\u306BPID\u30AD\u30E3\u30EA\u30D6\u30EC\u30FC\u30B7\u30E7\u30F3\u3092\u5B9F\u65BD\npid_kp: 68.453\npid_ki: 2.749\npid_kd: 426.122\n\n#####################################################################\n# \u30D5\u30A1\u30F3\u5236\u5FA1\n#####################################################################\n[fan]\npin: gpio16\nmax_power: 1.0\nkick_start_time: 0.5                                                # \u30D5\u30A1\u30F3\u304C\u8D77\u52D5\u3057\u306A\u3044\u5834\u5408\u306F\u3053\u306E\u5024\u3092\u5897\u3084\u3057\u3066\u304F\u3060\u3055\u3044\noff_below: 0.13\ncycle_time: 0.010\n#####################################################################\n[heater_fan hotend_fan]\npin: gpio17\nmax_power: 1.0\nkick_start_time: 0.5\nheater: extruder\nheater_temp: 50.0\n\n#####################################################################\n#   \u30D5\u30A3\u30E9\u30E1\u30F3\u30C8\u5207\u308C\u30BB\u30F3\u30B5\u30FC\n#####################################################################\n#[filament_switch_sensor Filament_Runout_Sensor]\n#pause_on_runout: True\n#runout_gcode: PAUSE\n#switch_pin: gpio11\n\n\n#####################################################################\n# \u30DB\u30FC\u30DF\u30F3\u30B0\u3068\u30AC\u30F3\u30C8\u30EA\u30FC\u8ABF\u6574\u30EB\u30FC\u30C1\u30F3\n#####################################################################\n\n[idle_timeout]\ntimeout: 1800\n\n[homing_override]\naxes: xyz\nset_position_z: 0\ngcode:\nG90\nG0 Z5 F600\n{% set home_all = 'X' not in params and 'Y' not in params and 'Z' not in params %}\n\n{% if home_all or 'X' in params %}\n    _HOME_X\n{% endif %}\n    \n{% if home_all or 'Y' in params %}\n    _HOME_Y\n{% endif %}\n    \n{% if home_all or 'Z' in params %}\n    _HOME_Z\n{% endif %}\n\n\n#[safe_z_home]                      V0.0\u307E\u305F\u306FV0.1\u306EZ\u30A8\u30F3\u30C9\u30B9\u30C8\u30C3\u30D7\u4F4D\u7F6E\u3092\u4F7F\u7528\u3059\u308B\u5834\u5408\u306E\u307F\u5FC5\u8981\n#home_xy_position: 120,120\n#speed: 50.0\n#z_hop: 5\n\n## BED_SCREWS_ADJUST\u3068\u4F75\u7528\n[bed_screws]\nscrew1: 60,5\nscrew1_name: \u524D\u30CD\u30B8\nscrew2: 5,115\nscrew2_name: \u5DE6\u5F8C\u308D\nscrew3: 115,115\nscrew3_name: \u53F3\u5F8C\u308D\nProbe_height: 0.1\n\n#####################################################################\n# \u30DE\u30AF\u30ED\n#####################################################################\n\n[gcode_macro PRINT_START]\n#   \u30B9\u30E9\u30A4\u30B5\u30FC\u306E\u958B\u59CB\u30B9\u30AF\u30EA\u30D7\u30C8\u3067PRINT_START\u3092\u4F7F\u7528 # \u304A\u4F7F\u3044\u306E\u30B9\u30E9\u30A4\u30B5\u30FC\u306B\u5408\u308F\u305B\u3066\u30AB\u30B9\u30BF\u30DE\u30A4\u30BA\u3057\u3066\u304F\u3060\u3055\u3044\ngcode:\n    G28                            ; \u5168\u8EF8\u30DB\u30FC\u30E0\n    G90                            ; \u7D76\u5BFE\u4F4D\u7F6E\u6307\u5B9A    \n    G1 Z20 F3000                   ; \u30CE\u30BA\u30EB\u3092\u30D9\u30C3\u30C9\u304B\u3089\u96E2\u3059\n\n[gcode_macro PRINT_END]\n#   \u30B9\u30E9\u30A4\u30B5\u30FC\u306E\u7D42\u4E86\u30B9\u30AF\u30EA\u30D7\u30C8\u3067PRINT_END\u3092\u4F7F\u7528 - \u304A\u4F7F\u3044\u306E\u30B9\u30E9\u30A4\u30B5\u30FC\u306B\u5408\u308F\u305B\u3066\u30AB\u30B9\u30BF\u30DE\u30A4\u30BA\u3057\u3066\u304F\u3060\u3055\u3044\ngcode:\n    M400                           ; \u30D0\u30C3\u30D5\u30A1\u30AF\u30EA\u30A2\u5F85\u3061\n    G92 E0                         ; \u30A8\u30AF\u30B9\u30C8\u30EB\u30FC\u30C0\u30FC\u30BC\u30ED\n    G1 E-4.0 F3600                 ; \u30D5\u30A3\u30E9\u30E1\u30F3\u30C8\u30EA\u30C8\u30E9\u30AF\u30C8\n    G91                            ; \u76F8\u5BFE\u4F4D\u7F6E\u6307\u5B9A\n\n    #   \u5883\u754C\u53D6\u5F97\n    {% set max_x = printer.configfile.config[\"stepper_x\"][\"position_max\"]|float %}\n    {% set max_y = printer.configfile.config[\"stepper_y\"][\"position_max\"]|float %}\n    {% set max_z = printer.configfile.config[\"stepper_z\"][\"position_max\"]|float %}\n\n    #   \u7D42\u4E86\u4F4D\u7F6E\u306B\u5FDC\u3058\u3066\u5B89\u5168\u306A\u79FB\u52D5\u65B9\u5411\u3092\u6C7A\u5B9A\n    {% if printer.toolhead.position.x < (max_x - 20) %}\n        {% set x_safe = 20.0 %}\n    {% else %}\n        {% set x_safe = -20.0 %}\n    {% endif %}\n\n    {% if printer.toolhead.position.y < (max_y - 20) %}\n        {% set y_safe = 20.0 %}\n    {% else %}\n        {% set y_safe = -20.0 %}\n    {% endif %}\n\n    {% if printer.toolhead.position.z < (max_z - 2) %}\n        {% set z_safe = 2.0 %}\n    {% else %}\n        {% set z_safe = max_z - printer.toolhead.position.z %}\n    {% endif %}\n\n    G0 Z{z_safe} F3600             ; \u30CE\u30BA\u30EB\u3092\u4E0A\u3052\u308B\n    G0 X{x_safe} Y{y_safe} F20000  ; \u7CF8\u5F15\u304D\u3092\u9632\u3050\u305F\u3081\u30CE\u30BA\u30EB\u79FB\u52D5\n    TURN_OFF_HEATERS\n    M107                           ; \u30D5\u30A1\u30F3\u30AA\u30D5\n    G90                            ; \u7D76\u5BFE\u4F4D\u7F6E\u6307\u5B9A\n    G0 X60 Y{max_y-10} F3600          ; \u30CE\u30BA\u30EB\u3092\u5F8C\u65B9\u306B\u30D1\u30FC\u30AF\n\n[gcode_macro LOAD_FILAMENT]\ngcode:\nM83                            ; \u30A8\u30AF\u30B9\u30C8\u30EB\u30FC\u30C0\u30FC\u3092\u76F8\u5BFE\u30E2\u30FC\u30C9\u306B\nG1 E30 F300                    ; \u30D5\u30A3\u30E9\u30E1\u30F3\u30C8\u30ED\u30FC\u30C9\nG1 E15 F150                    ; \u30CE\u30BA\u30EB\u306B\u30D5\u30A3\u30E9\u30E1\u30F3\u30C8\u3092\u30D7\u30E9\u30A4\u30E0\nM82                            ; \u30A8\u30AF\u30B9\u30C8\u30EB\u30FC\u30C0\u30FC\u3092\u7D76\u5BFE\u30E2\u30FC\u30C9\u306B\n        \n[gcode_macro UNLOAD_FILAMENT]\ngcode:\nM83                            ; \u30A8\u30AF\u30B9\u30C8\u30EB\u30FC\u30C0\u30FC\u3092\u76F8\u5BFE\u30E2\u30FC\u30C9\u306B\nG1 E10 F300                    ; \u5148\u7AEF\u3092\u67D4\u3089\u304B\u304F\u3059\u308B\u305F\u3081\u5C11\u3057\u62BC\u3057\u51FA\u3057\nG1 E#40 F1800                  ; \u5C11\u3057\u30EA\u30C8\u30E9\u30AF\u30C8\uFF08\u3084\u308A\u3059\u304E\u308B\u3068\u8A70\u307E\u308B\u306E\u3067\u6CE8\u610F\uFF09\nM82                            ; \u30A8\u30AF\u30B9\u30C8\u30EB\u30FC\u30C0\u30FC\u3092\u7D76\u5BFE\u30E2\u30FC\u30C9\u306B\n\n[gcode_macro _HOME_X]\ngcode:\n    # \u30BB\u30F3\u30B5\u30FC\u30EC\u30B9\u30DB\u30FC\u30DF\u30F3\u30B0\u6642\u306FA/B\u30B9\u30C6\u30C3\u30D1\u30FC\u306Erun_current\u3092\u7D71\u4E00\n    {% set RUN_CURRENT_X = printer.configfile.settings['tmc2209 stepper_x'].run_current|float %}\n    {% set RUN_CURRENT_Y = printer.configfile.settings['tmc2209 stepper_y'].run_current|float %}\n    {% set HOME_CURRENT_RATIO = 0.7 %} # \u30C7\u30D5\u30A9\u30EB\u30C8\u3067\u30DB\u30FC\u30DF\u30F3\u30B0\u6642\u306B\u30E2\u30FC\u30BF\u30FC\u96FB\u6D41\u3092\u4E0B\u3052\u307E\u3059\u3002\u30B9\u30AD\u30C3\u30D7\u3059\u308B\u5834\u5408\u306F\u3053\u306E\u5024\u3092\u8ABF\u6574\u3057\u3066\u304F\u3060\u3055\u3044\n    SET_TMC_CURRENT STEPPER=stepper_x CURRENT={HOME_CURRENT_RATIO * RUN_CURRENT_X}\n    SET_TMC_CURRENT STEPPER=stepper_y CURRENT={HOME_CURRENT_RATIO * RUN_CURRENT_Y}\n\n    # \u30DB\u30FC\u30E0\n    G28 X\n    # \u96E2\u308C\u308B\n    G91\n    G1 X#10 F1200\n        \n    # StallGuard\u30EC\u30B8\u30B9\u30BF\u30AF\u30EA\u30A2\u5F85\u3061\n    M400\n    G90\n    # \u5370\u5237\u6642\u306E\u96FB\u6D41\u8A2D\u5B9A\n    SET_TMC_CURRENT STEPPER=stepper_x CURRENT={RUN_CURRENT_X}\n    SET_TMC_CURRENT STEPPER=stepper_y CURRENT={RUN_CURRENT_Y}\n\n[gcode_macro _HOME_Y]\ngcode:\n    # \u30BB\u30F3\u30B5\u30FC\u30EC\u30B9\u30DB\u30FC\u30DF\u30F3\u30B0\u6642\u306E\u96FB\u6D41\u8A2D\u5B9A\n    {% set RUN_CURRENT_X = printer.configfile.settings['tmc2209 stepper_x'].run_current|float %}\n    {% set RUN_CURRENT_Y = printer.configfile.settings['tmc2209 stepper_y'].run_current|float %}\n    {% set HOME_CURRENT_RATIO = 0.7 %} # \u30C7\u30D5\u30A9\u30EB\u30C8\u3067\u30DB\u30FC\u30DF\u30F3\u30B0\u6642\u306B\u30E2\u30FC\u30BF\u30FC\u96FB\u6D41\u3092\u4E0B\u3052\u307E\u3059\u3002\u30B9\u30AD\u30C3\u30D7\u3059\u308B\u5834\u5408\u306F\u3053\u306E\u5024\u3092\u8ABF\u6574\u3057\u3066\u304F\u3060\u3055\u3044\n    SET_TMC_CURRENT STEPPER=stepper_x CURRENT={HOME_CURRENT_RATIO * RUN_CURRENT_X}\n    SET_TMC_CURRENT STEPPER=stepper_y CURRENT={HOME_CURRENT_RATIO * RUN_CURRENT_Y}\n\n    # \u30DB\u30FC\u30E0\n    G28 Y\n    # \u96E2\u308C\u308B\n    G91\n    G1 Y#10 F1200\n\n    # StallGuard\u30EC\u30B8\u30B9\u30BF\u30AF\u30EA\u30A2\u5F85\u3061\n    M400\n    G90\n    # \u5370\u5237\u6642\u306E\u96FB\u6D41\u8A2D\u5B9A\n    SET_TMC_CURRENT STEPPER=stepper_x CURRENT={RUN_CURRENT_X}\n    SET_TMC_CURRENT STEPPER=stepper_y CURRENT={RUN_CURRENT_Y}\n\n[gcode_macro _HOME_Z]\ngcode:\n    G90\n    G28 Z\n    G1 Z30\n\n"})})]})}function d(n={}){let{wrapper:e}={...(0,o.a)(),...n.components};return e?(0,i.jsx)(e,{...n,children:(0,i.jsx)(c,{...n})}):c(n)}},50065:function(n,e,t){t.d(e,{Z:()=>_,a:()=>s});var r=t(67294);let i={},o=r.createContext(i);function s(n){let e=r.useContext(o);return r.useMemo(function(){return"function"==typeof n?n(e):{...e,...n}},[e,n])}function _(n){let e;return e=n.disableParentContext?"function"==typeof n.components?n.components(i):n.components||i:s(n.components),r.createElement(o.Provider,{value:e},n.children)}}}]);